style.css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("meneame.net") {
 
  /**
   * Cabecera superior sin desplazamiento
   */
  .header-top-wrapper {
    position: absolute !important;
  }
  
  /**
   * Información de usuario
   */
  ul#userinfo {
    background-color: #C43E0B;
    right: 0px;
    z-index: 1;
    border-radius:  5px;
  }
  i.fa.fa-search {
    margin-left: 5px;
  }
  input.searchbox {
    z-index: 1;
  }

  /**
   * Notificaciones en rojo
   */
  #header-top #notifier.nonzero {
    color: #f00;
  }

  /**
   * Sin noticia destacada
   */
  #toplink {
    display:none;
  }
 
  /**
   * Sin barra lateral y con el cuerpo principal sin límite de tamaño
   */
  #sidebar {
    display:none;
  }
 
  #newswrap {
    margin: 0px 180px 180px 0px;
  }
  
   /**
   * Modificación de colores para aumentar el contraste
   */
  div#wrap {
    background-color: #E9E9E9;
  }
  div.header-menu-wrapper {
    background-color: #D6D6D6;
    border-bottom: 1px solid #C0C0C0;
  }
  fieldset {
    background-color: #EDD8C2;
  }
  .news-shakeit{
    background-color: #EDD8C2;
  }
  
  /**
   * Ignore total: sugerido por: https://www.meneame.net/notame/BBE
   * Con esto no te aparecen las notas ni los comentarios de los usuarios que tengas ignorados :->
   * La parte mala es que también borra los blanqueados por votos.
   */
  /**  <-- borra esta línea para descomentar junto con la otra indicada
  .comment-body.phantom {
    display: none !important;
  }
  .comment-meta.phantom {
    display: none !important;
  }
       --> borra está línea para descomentar junto con la otra indicada */
}
