style.css:
@-moz-document url("chrome://browser/content/browser.xul"){

#toolbar-menubar {
 position:fixed !important; top:0 !important; z-index:9 !important;
 padding:0 0 0 9px !important; border:none !important;
 background:no-repeat bottom left url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAANUlEQVQ4jWNgoBL4j4ZHogHIBlEE/qOxSXYVuiayDKDYBRQboECsYpq5gKJAxGcY/Q0gCgAAN9M75XUMKhYAAAAASUVORK5CYII=) !important;
}
#toolbar-menubar:hover {
 background-color:menu !important;
 background-position:center left !important;
}
#toolbar-menubar:not(:hover) {
 min-height:1px !important; height:6px !important
}
#toolbar-menubar:not(:hover) #main-menubar > menu {
 min-height:1px !important; height:2px !important;
}

}
