style.css:
@-moz-document domain("bbs.io-tech.fi") {
/************************************************/
/*      IO-Light theme for TechBBS (2016)       */
/*      for Firefox & Chrome with Stylish       */
/* -------------------------------------------  */
/*     created by Lifeless - rmj@3rdw.net       */
/* -------------------------------------------  */
/*      version: 0.13 beta - 2019-07-30         */
/* -------------------------------------------  */
/*            No Lilies Were Harmed             */
/*       During the Making of This Style        */
/* -------------------------------------------  */
/*                 nos univers                  */
/*               A Contre-Courant               */
/*               tout à l'envers                */
/*              pas pour longtemps              */
/************************************************/
  
/************************************************/
/*DEFAULTS (DO NOT TOUCH, USE THE OPTIONS BELOW)*/  
html { background:#fff!important; }
.pageWidth { margin:0!important; }
/*DEFAULTS (DO NOT TOUCH, USE THE OPTIONS BELOW)*/  
/************************************************/
  
  
/************************************************/
/*                                              */
/*                  OPTIONS                     */
/*                                              */
/************************************************/

/* --- TAUSTAVARI --------------------------------------------------- */
/* - Varit ilmoitetaan muodossa:                                      */
/*        #rgb (r=red g=green b=blue, hex arvot valilta 0-f)          */
/*        #rrggbb (kuten ylempi, mutta arvot valilta 00-ff)           */
/*        color (esim: white, brown, blue, gold, silver, tan)         */
/*        ESIM (puna-oranssi):   background:#ff9900                   */
/*        ESIM (musta):          background:black                     */
html { background:rgba(255,255,255,1)!important; }

/* --- YLIMAARAISET TYHJAT REUNUKSET FOORUMIN YMPARILLE ------------- */
/* - numerot tarkoittaa reunoja jarjestyksessa: TOP RIGHT BOTTOM LEFT */
/* - asetus voi olla '%' tai 'em'.                                    */
/*        Sivuille % toimii paremmin                                  */ 
/*        Alas toimii paremmin 'em' (1em on fontin korkeus            */
/*        ESIM:       margin:0 10% 0em 10%                            */
/*        ESIM:       margin:0 5% 2% 15%                              */
html { margin:0 0% 0 0% !important; }
    
/************************************************/
/*                                              */
/*               OPTIONS ENDS                   */
/*                                              */
/************************************************/  
  
/* BASIC STUFF FOR BACKGROUND */  
body {
  background:transparent!important;
}
#content .pageContent {
/*  border:none!important;
  background:transparent!important;*/
}  
/* BASIC STUFF FOR BACKGROUND ENDS */  

  
  html, body, .pageWidth {
    padding:0px!important;
    margin:0px!important;
  }
  
/* HEADER */
  #logoBlock {
    display: none!important;
  }
  .navTab, .navTab * {
/*    border: none!important; */
  }
  .navTab.home > a {
    background: rgba(20,40,60,1) url("https://www.io-tech.fi/favicon.ico") no-repeat !important;
    background-size: 20px 20px !important;
    background-position: 50% 50% !important;
    width: 50px !important;
  }
  .navTab.home > a {
    color: transparent !important;
    width: 20px!important;
    
  }
  .navTab.forums > a {
    background: rgba(20,40,60,1) url("https://bbs.io-tech.fi/images/techbbs.svg") 50% 50% no-repeat !important;
    background-size: 100px 40px !important;
    width: 130px !important;
  }
  .navTab.forums > a {
    color: transparent !important;
    width: 100px!important;
  }
  .navTab.home > a:hover, .navTab.forums > a:hover {
    opacity: 0.7;
  }
/* HEADER ENDS */
  
  
/* REMOVE SIDE BAR */
/*[[sidebar]]*/
.sidebar {
  display:none!important;
}
.mainContent {
  margin-right:0px!important;
}
/* REMOVE SIDE BAR ENDS */
  
  
  
/* FORUM NAVIGATION */  
/*[[forumnav]]*/
.breadBoxBottom .breadcrumb {
  position:fixed;
  bottom:0px;
  left:0px;
  width:100%;
  height:30px!important;
  padding:0px!important;
  border-radius:0;
  z-index:90;
  border-width: 1px 0 0 0!important;
}  
.breadBoxBottom .arrow {
  border-top-width:14px!important;
  border-bottom-width:14px!important;
  border-left-width:7px!important;
}
.breadBoxBottom .crumb {
  line-height:28px!important;
}
.breadBoxBottom .jumpMenuTrigger {
  line-height:28px!important; 
}
.jumpMenuTrigger:before {
  content:"";
}
#content {
  padding-bottom: 30px!important;
}
/* FORUM NAVIGATION ENDS */  

  
  
  

/* NOTICES */
.Notices {
  z-index:10000!important;
  width:70px!important;
  height:30px;
  position:fixed;
  top:0px;
  left:50%!important;
  opacity:1;
  overflow:hidden;
  border-radius:0 0 5px 5px;
/*  display:none!important; */
}
.Notices:hover {
  width:50%!important;
  height:100%;
  left:25%!important;
}
.PanelScroller .scrollContainer    {
  font-size:8pt!important;
  width:500px!important;
}
.baseHtml.noticeContent:before {
  content:'ilmoitukset: \013';
}  
/* NOTICES ENDS */

/* THREAD LIST */
  .prefixLink * {
    border: none!important;
    border-radius: 10px !important;
    opacity: 0.6;
  }
/* THREAD LIST ENDS */
  
/* MESSAGE */
  LI.message {
    margin-top: 30px!important;
    background: rgba(235,250,255,1)!important;
    border: 1 solid rgba(0,0,0,0.1)!important;
  }
  LI.message * {
    border: none!important;
  }
  .messageHeading {
    background: rgba(63, 93, 103,1)!important;
  }
  .messageHeading .datePermalink {
    position: relative!important;
    left: 290px!important;
  }
  LI.message .messageContent {
    padding: 10px!important;
  }
  LI.message .messageContent * {
    font: 14px Verdana !important;
  }
  LI.message .messageContent  {
    width: 98% !important;
    overflow: visible!important;
  }
  LI.message .messageContent .newIndicator {
    position: relative !important;
    left: -50px !important;
    top: -45px !important;
  }
  LI.message .messageContent .quoteContainer {
  }
  LI.message .signature ASIDE {
    border-top: 1px dashed rgba(0,0,0,0.1)!important;
    clear: both!important;
    margin: 5px 20px 5px 10px !important;
    padding: 5px!important;
    color: #ccc!important;
    font-size:10px!important;
  }
  LI.message .signature ASIDE IMG { 
    display: none!important;
  }
  LI.message .signature ASIDE * { 
    color: #ccc!important;
    font-size:10px!important;
  }
  li.message .item.edit{
  position:absolute;
  right:145px!important;
  }
  .messageInfo.primaryContent {
    border: none!important;
    margin: 0!important;
  }
  .messageContent {
    float: left!important;
  }
div[id*="likes-post"] {
    display: block !important;
}
div[id*="likes-post"] * {
    display: block !important;
    width: 100%;
}
div[id*="likes-post"] a {
    display: inline-block !important;
    width: auto;
}
/* MESSAGE ENDS */
  
 
/* MEMBER INFO */
  .messageUserInfo * {
    background: transparent!important;
    display: inline-block!important;
    vertical-align: top!important;
  }
  .messageUserInfo {
/*    float: left!important;*/
    display: block!important;
    position: relative!important;
    border: none!important;
    height: 0!important;
    background: #fff!important;
  }
  LI.message .messageUserBlock {
    background: #fff!important;
    width: 290px!important;
    height: 40px!important;
    border-radius:20px 20px 20px 0;
    box-shadow: -2px 5px 3px rgba(0,0,0,0.4);
    top: -40px!important;
    left: -5px!important;
    border: 1px solid rgba(0,0,0,0.1)!important;
  }  
  LI.message .messageUserBlock .xbJoinDate {
    margin-left:25px!important
  }
  .messageUserBlock .arrow {
    display: none!important;
  }
  .messageUserInfo .avatarHolder {
/*    background: rgba(255,255,255,1)!important;
    width: 60px!important;
    height: 69px!important; */
    position: relative!important;
    left: -20px!important;
    top: -25px!important;
    float: left!important;
  }
  .messageUserInfo .avatarHolder IMG {
    border-radius: 50% 50% 50% 50%;
    box-shadow: -3px 5px 3px rgba(0,0,0,0.4); 
    width: 75px!important;
    height: 75px!important;
    border: none!important;
    position:relative!important;
    padding: 5px!important;
    background: #fff!important;
    border: 1px solid rgba(0,0,0,0.1)!important;
  } 
  .messageUserInfo .userBanner, .messageUserInfo .userTitle {
    display:none!important;    
  }
  H3.userText {
    float: left!important;
    position: relative;
    left: -30px!important;
    top: -2px!important;
    width:180px!important;
    height: 16px!important;
    overflow: hidden!important;
    text-align: left!important;
  }
  DIV.extraUserInfo {
    position: absolute!important;
    left: 68px!important;
    top: 16px!important;
    display: inline-block!important;
  }
  .extraUserInfo table {
    display: none!important;
  }
/* MEMBER INFO ENDS */

/* QUICK REPLY */
  .quickReply.message .avatarHolder {
    position: relative !important;
    left: 30px !important;
    top: 0px !important;
  }
  .quickReply.message IMG {
    width: 96px !important;
    height: 96px !important;
    box-shadow: none!important;
    padding: 0!important;
  }
/* QUICK REPLY ENDS */   
  
 
/* FOOTER */  
  .footer * {
    padding:0!important;
    margin:0!important;
  }
  .footer .pageContent {
    height:50px!important;
  }
  .footer .pageContent .choosers {
    float:right!important;
    padding:2px!important;
  }
  .footer .pageContent .choosers dd {
    padding:0 10px 0 10px!important;
  }
  .footer .pageContent .footerLinks {
    float:right!important;
    clear:both!important;
    padding:2px!important;
  }
  .footer .pageContent .footerLinks li {
    padding:0 10px 0 10px!important;
  }
  .extraFooter {
    background:transparent!important;
    position:relative;
    display:block!important;
    left:0px;
    top:-63px;
    width:300px;
  }
  .extraFooter * {
    padding:0!important;
    margin:0!important;
  }
  .extraFooter .fa { display:none!important; }
  .extraFooter h3 { font-size:10pt; }
  .extraFooter .footerBlockOne img {
    height:26px;
  }
  .extraFooter .footerBlockTwo {
    padding-left:50px!important;
  }
  .extraFooter .footerBlockTwo ul {
    width:200px!important;
  }
  .extraFooter .footerBlockTwo li {
    display:inline-block!important;
    padding: 0 15px 0 5px !important;
    font-size:16px;
    font-weight:bold;
  }
  .footerLegal {
    display:none!important;
  }
/* FOOTER ENDS */  
  
/* ADS */
  #mainos, #premium {
    display: none!important;
  }
/* ADS ENDS */
  
  
/* END OF STYLE */
}
