style.css:
@-moz-document domain("smogon.com") {
/* Version: 1.13 */
html,
body,
.p-body {
    /* Header */

    background: rgb(40, 40, 40);
    color: rgb(200, 200, 200);
}
/* remove underline from date/time */

[class="DateTime"] {
    text-decoration: none !important
}
[class="DateTime muted lastThreadDate"] {
    text-decoration: none !important
}
[class="DateTime muted"] {
    text-decoration: none !important
}
#content .pageContent {
    background-color: rgb(85, 85, 85);
}
/* ##### Moderator Bar ##### */

.hScroller-scroll.is-calculated {
    background-color: rgb(30, 30, 30);
}
.hScroller-scroll.is-calculated .p-staffBar-link.menuTrigger:hover {
    background-color: rgb(0, 0, 0);
}
.p-staffBar {
    background: rgb(30, 30, 30);
    border-color: rgb(0, 0, 0);
}
.menu.menu--left .menu-arrow {
    border-bottom-color: rgb(255, 255, 255);
}
.menu-content {
    border-top: 3px solid rgb(255, 255, 255);
}
.menu-header {
    background: rgb(0, 0, 0);
}
a.menu-linkRow {
    background-color: rgb(30, 30, 30);
}
.menu-content a.menu-linkRow:hover {
    color: rgb(255, 255, 255);
    border-left-color: rgb(100,0,0);
}
/* Moderator Bar end */
/* ##### Logo Background ##### */

.p-header {
    background: rgb(30, 30, 30) !important;
}
/* ##### Top Navigation Bar ##### */
/* home, forums, etc selected tab color */

.p-nav-list .p-navEl.is-selected {
    position: relative;
    font-weight: 700;
    background: rgb(60, 0, 0);
    padding-top: 2px;
    margin-top: -2px;
    border-width: 1px;
    /*border-style: solid solid none;
    border-color: rgb(200,200,200);
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;*/
}
/* top Nav bar BG color */

.p-nav-inner,
.p-nav {
    background-color: rgb(30, 30, 30);
}
/* arrow color */

.p-navEl-splitTrigger::after {
    color: rgb(255, 255, 255);
}
a.p-navE1-splitTrigger:hover {
    background-color: rgb(0, 255, 0);
}
/* tab color when arrow is clicked */

.p-nav-list .p-navEl.is-menuOpen {
    color: rgb(255, 255, 255);
    background: rgb(10, 10, 10);
}
/* username/inbox/alerts BG */

.p-nav-opposite {
    background: rgb(10, 10, 10);
}
/* section links */

.p-sectionLinks,
.p-sectionLinks-list {
    background-color: rgb(60, 0, 0);
}
/* section links with arrow selected */

.p-sectionLinks-list .p-navEl.is-menuOpen {
    background-color: rgb(40, 0, 0);
}
/* menu when you click username*/

.menu-row.menu-row--highlighted {
    background: rgb(0, 0, 0);
    color: rgb(255, 255, 255);
}
/* random rectangle in said menu */

ul.listPlain:nth-child(3) {
    background: rgb(30, 30, 30);
}


/* Top Navigation Bar end */
/* ##### Background on forums page ##### */
/* Subforum title lines */

.block-header {
    color: rgb(255, 255, 255);
    background: rgb(0, 0, 0);
    border-bottom: 1px solid rgb(60,60,60);
}
/* Subforum title line link color */

.block-header a {
    color: rgb(0, 0, 0);
}
/* Subfourm forum list colors */

.node-body {
    background: rgb(40,0,0);
    color: rgb(255, 255, 255);
}
/* unread subfourm */

.node--unread .node-body {
    background: rgba(40, 0, 0, 0.85);
}
/* balloon color for unread message */

.node--unread .node-icon i::before,
.subNodeLink.subNodeLink--unread::before {
    color: #fcc375;
}
.pairs.pairs--inline dt,
.pairs.pairs--rows dt {
    background: transparent;
}
/* Background on forums page end */

.u-scrollButtons,
.button.button--scroll,
a.button.button--scroll {
    background: rgb(40, 40, 40);
}

/* scroll top bar */
i.hScroller-action.hScroller-action--end.is-active {
    background: linear-gradient(to right, rgba(40,0,0,0.85) 0%, rgb(100,0,0) 33%);
}

i.hScroller-action.hScroller-action--start.is-active {
    background: linear-gradient(to right, rgb(100,0,0) 66%, rgba(40,0,0,0.85) 100%);
}

.lSSlideOuter .lSPager.lSpg > li:hover a, .lSSlideOuter .lSPager.lSpg > li.active a{
    background-color: rgb(170,0,0);
}

/* show older forums */
.structItem.structItem--note {
    background: rgb(0,0,0);
}
/* ##### Links on Top of home page ##### */
/* About, Dex, Forums, IRC background color */

#header_island,
#about,
#search_help {
    background: rgb(85, 85, 85);
}
/* About, Dex, etc border */

#header_island {
    border: 1px solid rgb(0, 0, 0) !important;
    border-radius: 12px;
}
/* About, Dex, etc link color */

#header_island a {
    color: rgb(255, 255, 255);
}
/* Links on top of main page end */
/* ##### Search Box on Main Page ##### */

input[name="q"] {
    background: rgb(111, 111, 111);
    border: 1px solid rgb(111, 111, 111);
    color: rgb(200, 200, 200);
}
/* Search Box on Main Page End */
/* ##### Search button on main page ##### */

input[value="Search"] {
    border: 1px solid rgb(40, 40, 40);
    background-color: rgb(40, 40, 40);
    color: rgb(200, 200, 200);
}
/* Search button on main page end */
/* twitter widget */

.timeline-Widget {
    background-color: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
}
.timeline-Header,
.timeline-Header-title {
    background-color: rgb(0, 0, 0);
    color: rgb(255, 255, 255);
}
/* ##### Link Color ##### */
/* link color */

a:link,
a:visited,
a.primaryContent:link,
a.primaryContent:visited {
   color: rgb(255,255,255);
}
/* link hover */

a:link:not(.Type):not(.jumpMenuTrigger):not([class*="redactor_btn"]):not([class*="feed"]):not([class*="Feed"]):hover,
a:visited:hover,
a.primaryContent:link:hover,
a.primaryContent:visited:hover {
    background: transparent;
    /*color: rgba(100,127,148,0.5);*/
    
    text-shadow: 0px 0px 5px rgba(0, 173, 238, .5);
}
/* selected link color */

a:focus {
    color: rgb(255, 255, 255);
}
/* unread links */

.discussionListItems .unread .title a {
    color: rgba(100, 0, 0, 0.75);
}

/* internal links */

a.link.link--internal, a.link.link--external {
    color: rgb(76,186,178);
    
}

/* links on signature menu */

a.secondaryContent:hover {
    background: rgb(60, 60, 60);
}
/* Link color end */
/* ##### Search ##### */
/* Search popup background and text color */

.menu-content {
    background: rgb(40, 40, 40);
    color: rgb(255, 255, 255);
}
/* search selected color */

.p-navgroup-link.is-menuOpen {
    color: rgb(255, 255, 255);
    background-color: rgb(0, 0, 0);
}
/* search menu arrow */

.menu-arrow {
    border-bottom-color: rgb(255, 255, 255);
}
/* search input field */

.input {
    background: rgb(60, 60, 60);
    color: rgb(255, 255, 255);
}
/* search titles only checkbox */

label.iconic > input + i::before, label.iconic:hover > input + i::before {
    color: rgb(255, 255, 255);
}
/* search contraints */

.js-quickSearch-constraint {
    background: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
}
/* Search popup search button */

.button.button--primary {
    text-shadow: none;
    box-shadow: none;
    background: rgb(70, 70, 70);
    color: rgb(255, 255, 255);
    border-color: rgb(255, 255, 255);
}
.button--primary:hover,
.button--primary:focus {
    background-color: rgb(40, 40, 40) !important;
    color: rgb(255, 255, 255);
}
/* Search popup Advanced search button */

a.button {
    text-shadow: none;
    box-shadow: none;
    background: rgb(70, 70, 70);
    border-color: rgb(255, 255, 255);
    color: rgb(255, 255, 255);
}
a.button:hover,
a.button:focus {
    background: rgb(40, 40, 40) !important;
    color: rgb(255, 255, 255);
}
.menu-footer {
    background: rgb(0, 0, 0);
}
/* calendar */

.pika-single,
.pika-label {
    background: rgb(40, 40, 40);
    color: rgb(200, 200, 200);
}
.pika-next,
.pika-prev,
.pika-table th,
.inputDate .inputDate-icon {
    color: rgb(255, 255, 255);
}
/* days from other month */

.is-outside-current-month .pika-button.pika-day {
    background: rgb(100, 100, 100);
    color: rgb(255, 255, 255);
}
/* days in current month */

.pika-button.pika-day {
    background: rgb(200, 200, 200);
}
/* selected day */

.is-selected > button:nth-child(1) {
    background: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
}
/* today */

.is-today .pika-button.pika-day {
    color: rgb(90, 0, 0);
}
/* search tabs BG */

.block-tabHeader.tabs.hScroller {
    background: rgb(0, 0, 0);
}
/* search artive tab */

.block-tabHeader .tabs-tab.is-active {
    color: rgb(255, 255, 255);
    border-color: rgb(50, 0, 0);
    background-color: rgb(50,0,0);
}
/* search page BG color */

.block-body,
.block-body dt {
    background: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
}
/* border below tabs on search page */

.block-tabHeader.tabs {
    border-bottom: 1px solid rgb(100, 100, 100);
}

.formSubmitRow-bar {
    background: rgb(0, 0, 0);
}
/* submit button selected text */

.formSubmitRow-controls > button:nth-child(1):focus {
    background: rgb(40, 40, 40) !important;
}
/* +/- buttn on search threads tab */

button.inputGroup-text:nth-child(2),
button.inputGroup-text:nth-child(3) {
    background: rgb(70, 70, 70);
    color: rgb(255, 255, 255);
}
button.inputGroup-text:nth-child(2):hover,
button.inputGroup-text:nth-child(3):hover,
button.inputGroup-text:nth-child(2):focus,
button.inputGroup-text:nth-child(3):focus {
    background: rgb(40, 40, 40) !important;
    color: rgb(255, 255, 255) !important;
}

/* search error */
.blockMessage.blockMessage--warning {
    border-left: 3px solid rgb(255,255,255);
    background: rgb(100,0,0);
    color: rgb(255,255,255);
}
/* Search end */
/* ##### Footer ##### */

#footer {
    background: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
}

/* users viewing */
#uaThreadViewContainer h3, #uaThreadReadContainer h3 {
    color: rgb(255,255,255);
    border-bottom: 1px solid rgb(255,255,255);
}

#uaThreadViewContainer h3 .footnote, #uaThreadReadContainer h3 .footnote {
    color: rgb(255,255,255);
}

/* users viewing bg color */
.block-container {
    background: rgb(30,30,30);
    border-color: transparent;
}
/* Footer end */
/* ##### List of Online Staff ##### */
/* headers */

.block-minorHeader {
    background: rgb(30, 30, 30);
    border-bottom: 1px solid rgb(255, 255, 255);
    color: rgb(255, 255, 255);
}
/* body of staff online etc */

.p-body-sidebar .block-body {
    background: rgb(60, 60, 60);
    color: rgb(200, 200, 200);
}
/* username link color */

a.username {
    color: rgb(76,186,178) !important;
}
/*footer */

.block-footer {
    background: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
}
.pairs.pairs--justified dt {
    background: transparent;
}
.block-textHeader.block-textHeader--scaled {
    color: rgb(255, 255, 255);
}
/* Latest Post end */
/* ##### User Custom Titles ##### */

.pairsJustified dt,
.sidebar .avatarList .userTitle {
    color: rgb(255, 255, 255);
}
.sidebar .section .secondaryContent h3,
.sidebar .section .secondaryContent h3 a {
    color: rgb(220, 220, 220);
    /*text-shadow: 1px 1px rgb(136,155,173);*/
}
/* CT end */

/* Profile page about tab */

a#about.tabs-tab, a#latest-activity.tabs-tab, a#recent-content.tabs-tab, a.tabs-tab  {
    background: rgb(0, 0, 0);
}

a.tabs-tab:hover {
    background-color: rgb(0,0,0) !important;
}

a.tabs-tab.is-active {
    background-color: rgb(60,0,0) !important;
}

.block-row.block-row--separated + .block-row, .block-row.block-row--separated {
    background: rgb(30,30,30);
}

/* ##### Profile/Inbox/Alerts Menus ##### 
/* alerts BG */
.menu-row.menu-row--alt {
    background: rgb(40,40,40);
    color: rgb(255,255,255);
}

/* list of old alerts */
.block-row.block-row--alt {
    background: rgb(30,30,30);
    color: rgb(200,200,200);
}

.block-row.block-row--highlighted {
    background: rgb(40,0,0);
    color: rgb(255,255,255);
}

/* Reactions link color */
.reaction-text.js-reactionText {
    color: rgb(200,0,0);
}

/* your account / bookmarks tabs */
.menu-tabHeader .tabs-tab.is-active {
    color: rgb(255,255,255);
    border-color: rgb(100,0,0);
}

.menu-tabHeader .tabs-tab {
    color: rgb(255,255,255);
}

/*

 /* Alerts/inbox end */
/* ##### Post New Thread Button Wifi ##### */
/* border,body color */

.button.button--cta,
a.button.button--cta {
    background: rgb(60, 0, 0);
    border-color: rgb(0, 0, 0);
}
/*hover color */

.button.button--cta:hover,
a.button.button--cta:hover {
    background: rgb(120, 0, 0) !important;
}

.button--icon.button--icon--cancel {
    background: rgb(70,70,70);
    border-color: rgb(255,255,255);
    color: rgb(255,255,255);
}

.button--icon.button--icon--cancel:hover{
    background: rgb(40,40,40);
}


/* end post new thread button wifi */
/* ##### Profile Page Following/Followers Labels ##### */
/* heading colors */

.block-textHeader {
    color: rgb(255, 255, 255);
}
/* members xx is following header */

.overlay-title {
    background: rgb(30, 30, 30);
}
ol.block-body,
ol.block-body dd {
    background: rgb(30, 30, 30) !important;
    color: rgb(255, 255, 255);
}
/* Followers end */
/*
  .withSearch .navTabs .navTab.selected .blockLinksList {
    background: transparent !important;
    border: none !important;
  } 
  */
/* ##### profile page heading BG ##### */
/* heading BG */

.memberHeader-content--info {
    background: rgb(0, 0, 0);
}
/* profile name color */

.memberHeader-name {
    color: rgb(255, 255, 255);
}
/* profile header end */
/* ##### profile dropdowns/buttons ##### */
/* follow/ignore */

.button.button--link,
a.button.button--link {
    background: rgb(70, 70, 70);
    color: rgb(255, 255, 255);
}
/* dropdowns */

button.button--link.menuTrigger.button:hover,
button.button--link.menuTrigger.button:focus {
    background: rgb(40, 40, 40);
}
/* profile dropdowns end */
/* ##### Profile Posts ##### */
/* main posts */


.message-cell.message-cell--main {
    background: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
    border-left: 1px solid rgb(30,30,30);
}

.message-cell.message-cell--user {
    background: rgb(0,0,0);
    color: rgb(255,255,255)
}

.message-userArrow {
    border-right-color: rgb(100,100,100);
}

.message-userArrow::after {
    border-right-color: rgb(30,30,30);
}
/* comments */

.comment-avatar,
.comment-main,
.message-responseRow {
    background: rgb(60, 60, 60);
    color: rgb(255, 255, 255);
}
a.actionBar-action.actionBar-action--reply {
    color: rgb(255, 255, 255);
}

/* no posts on profile alert message */
.block-row.js-replyNoMessages {
    background: rgb(40,0,0);
    color: rgb(255,255,255);
}
/* profile posts end */
/* ##### Thread Display options ##### */
/* filterbar */

.block-filterBar {
    background: rgb(0, 0, 0);
}
/* filter button hover */

a.filterBar-menuTrigger:hover {
    background: rgb(100, 100, 100) !important;
}
/* page nav pages */

.pageNav-page,
.pageNav-jump.pageNav-jump--next,
.pageNav-jump.pageNav-jump--prev {
    background: rgb(60, 60, 60);
    color: rgb(255, 255, 255);
}
/* current page */

.pageNav-page.pageNav-page--current {
    background: rgb(60, 0, 0);
    color: rgb(255, 255, 255);
    border: 1px solid rgb(255, 255, 255);
}
/* page nav pages hover */

.pageNav-page:hover,
.pageNav-page a:focus {
    background: rgb(30, 30, 30);
}
/* current page hover */

.pageNav-page.pageNav-page--current:hover {
    background: rgb(90, 0, 0);
}
/* unread forms link color */

.is-unread .structItem-title a {
    color: rgb(220, 0, 0);
    font-weight: 500;
}

.is-unread .structItem-title a:hover {
    text-shadow: 0px 0px 5px rgba(200,200,200, .15)!important;
}
/* sticky color */

.structItem-status.structItem-status--sticky::before {
    color: rgb(180, 0, 0);
}
/* padlock */

.structItem-status.structItem-status--locked::before {
    color: #fcc375;
}
/* watching */

.structItem-status.structItem-status--watched::before {
    color: #2BA12D;
}
/* deleted */

.structItem-status.structItem-status--deleted::before {
    color: #e91393;
}
/* thread page nav */

.structItem-pageJump a {
    background: rgb(60, 60, 60);
    color: rgb(255, 255, 255);
}

/* page nav when window small */
.pageNavSimple-el.pageNavSimple-el--current {
    color: rgb(255,255,255);
    background: rgb(60,0,0);
}

.pageNavSimple-el.pageNavSimple-el--current:hover {
    color: rgb(255,255,255);
    background: rgb(90,0,0);
}

/* next page page nav when window too small */
.pageNavSimple-el.pageNavSimple-el--prev, .pageNavSimple-el.pageNavSimple-el--next {
    background: rgb(60,60,60);
    color: rgb(255,255,255);
}

/* page nav small window last page */
.pageNavSimple-el.pageNavSimple-el--first, .pageNavSimple-el.pageNavSimple-el--last {
    color: rgb(255,255,255);
}

.pageNavSimple-el.pageNavSimple-el--first:hover, .pageNavSimple-el.pageNavSimple-el--last:hover {
    border: none;
    color: rgb(255,255,255);
}

/* more options (like, edit) when window too small  */
.actionBar-action.actionBar-action--menuTrigger {
    color: rgb(255,255,255);
}
/* thread title */

.p-title {
    color: rgb(255, 255, 255);
}
/* selected items for moderation button hover/focus */

.button.button--link:hover,
.button.button--link:focus {
    background: rgb(30, 30, 30);
}
/* checkmark color */

.inlineModButton i::before {
    color: rgb(255, 255, 255);
}
/* number counter */

.inlineModButton-count.js-inlineModCounter {
    background: rgb(255, 0, 0);
    color: rgb(255, 255, 255);
    border: rgb(0, 0, 0);
}
/* selected thread mod bar */

.inlineModBar {
    background: rgb(40, 0, 0);
    color: rgb(255, 255, 255);
}
/* inline mod bar close */

.js-inlineModClose.button {
    background: rgb(60, 60, 60);
    color: rgb(255, 255, 255);
    border: 1px solid rgb(255, 255, 255);
}
.js-inlineModClose.button:hover,
.js-inlineModClose.button:focus {
    background: rgb(30, 30, 30);
    color: rgb(255, 255, 255);
    border: 1px solid rgb(255, 255, 255);
}
/* selected thread color */

.structItem.is-mod-selected {
    background: rgb(40, 0, 0, 0.85);
}
/* active tag */

.menuTrigger.menuTrigger--prefix {
    color: rgb(255, 255, 255);
}
/* new tag */

.message-newIndicator {
    background: rgb(200, 0, 0, 0.85);
}
/* snippets */

.tooltip--preview .tooltip-content {
    background: rgb(40, 40, 40);
    color: rgb(255, 255, 255);
}
/* edit post cancel button */

a.overlay-titleCloser.js-overlayClose {
    color: rgb(255, 255, 255);
}

.block-formSectionHeader, .block-formSectionHeader span  {
    background: rgb(40,0,0);
    color: rgb(255,255,255);
}

/* poll creation */
.input[disabled], .input.input--number[disabled] ~ .inputNumber-button {
    background: rgb(0,0,0);
}

button.inputGroup-text:nth-child(2):hover:disabled, button.inputGroup-text:nth-child(3):hover:disabled  {
    background: rgb(0,0,0)!important;
    color: rgb(140,140,140)!important;
}

/* poll vote results number */
a.fauxBlockLink-blockLink {
    color: rgb(255,255,255);
}

/* poll results item hover */
.pollResult.pollResult--showVoters:hover {
    background: rgb(40,0,0);
}

/* poll results userlist */
.pollResult-voters {
    background: rgb(50,50,50);
}
/* reddit embed */
.card {
    color: rgb(255,255,255);
}

/* reddit title color */
.card.reddit .reddit-title {
    color: rgb(255,255,255);
    text-decoration: underline;
}

/* reddit link color */
.card.reddit .reddit-self a {
    color: rgb(76,186,178);
}

/* subreddit */
.card.reddit ul.reddit-info li a.black {
    color: rgb(255,255,255);
}

/* divisor between threads */
.structItem {
    border-top: 1px solid rgb(100,100,100);
}
/* Thread display end */
/* ##### Conversations ##### */
/* unread message */

.structItem.structItem--conversation.is-unread.js-inlineModContainer .structItem-title {
    color: rgba(180, 0, 0, 0.85);
}
/* selected indicator */

.button--link.js-inlineModTrigger.button span {
    color: rgb(255, 255, 255);
}
/* unread messages */

.menu-row.menu-row--separated.menu-row--clickable.menu-row--highlighted {
    background: rgb(40, 0, 0, 0.85);
}
/* message hover */

.menu-row.menu-row--separated.menu-row--clickable:hover {
    background: rgb(80, 80, 80);
}

/* filter BG color */
.block-filterBar .filterBar-filterToggle {
    background: rgb(40,0,0);
}

.block-filterBar .filterBar-filterToggle:hover {
    background: rgb(100,0,0) !important;
}

/* user character alert */
.select2-results__options {
    background: rgb(40,40,40);
    color: rgb(255,255,255);
}
 /* field text color */
.select2-search__field {
	color: rgb(255,255,255);
}

/* selected user to start convo with */
.select2-results__option.select2-results__option--highlighted {
    background: rgb(40,0,0);
}

/* chosen users */
.select2 .select2-selection ul > li.select2-selection__choice {
    background: rgb(40,0,0);
    color: rgb(255,255,255);
}

/* Conversation end */
