style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.noip.com/confirm-host") {

  p {
    color: black ; 
  }  
  
  #way-header-wrap {
     background-color: #f0fcfe ; 
     box-shadow: 0px 0px  12px 0px  #333     !important;       
  }
     
:-moz-any-link ,
a[class="tip lightgrey"]  ,
A:link { 
     color:  #257AEA  !important; 
}
A:visited {
     color:  #6F2399  !important; 
}
A:hover {
    color:  #d8280E  !important; 
    text-decoration: underline !important;
}  
A:visited:hover {
     color:  #C1176E  !important;  
}   
     
     
a[class^="signup"] ,
a[class*="button"] , 
#way-header-wrap  a[class*="signup"] ,     
#way-header-wrap  a[class*="signup"][class*="grey"]   { 
     color:  #fff  !important; 
     background-color: #184DB2 !important; 
     border: none !important;  
     text-decoration:  none !important;  
}     
/*      grey */
a[class*="button"][class*="grey"]:hover  { 
     background-color: #CF4512 !important;      
}        
/*      green */
a[class^="signup"]:hover  ,
a[class*="button"]:hover ,      
a[class*="button"][class*="green"]:hover ,
#way-header-wrap  a[class*="signup"]:hover ,      
#way-header-wrap  a[class*="signup"][class*="grey"]:hover      { 
     background-color: #26A323 !important; 
}       
  
/*  No thanks, just renew my free hostname    */ 
  a[class="button bordered grey small botmarg"]  {
       background-color: #a00 !important;       
  }  
  a[class="button bordered grey small botmarg"]:hover  {
       background-color: #f00 !important;  
       color: #FFEE0E!important;     
  }  
     
     
.button.small { 
     text-decoration:  none !important;  
}        
.button.small:hover  { 
     background-color: #8527CF !important;      
     color:  #fff  !important; 
}        
     
}    /*  --- noip ---------------------------- */
