style.css:
@-moz-document domain("forum.yoyogames.com") {
.pageWidth {
	margin: 0 auto;
	width: /*[[page-width]]*/;
}
.message .messageInfo, .sectionMain, .discussionList, .messageText {
	background-color: #fefefe;
}
.discussionListItem .posterAvatar, .discussionListItem .stats,
.node .nodeLastPost,
.hasJs .bbCodeSpoilerText {
	background-color: #f3f3f3;
}
.discussionListItem.new {
	background-color: #fff9f5;
}
.discussionListItem.new .posterAvatar, .discussionListItem.new .stats {
	background-color: #ffefe0;
}
body .muted, body a.muted, body .muted a,
.searchResult .meta a, .searchResult .snippet a,
.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt, .pairsJustified dd,
.node .nodeDescription {
	color: #606060;
}
.messageText, html, .sectionMain {
	color: #121;
}
.discussionListItem {
	border-color: #c0c0c0;
}
.discussionListItem .stats dl {
	border-color: transparent;
}
html {
	background: #E6E6E6 url('data:image/png;base64,') top center;
}
html body[contenteditable] { background: white; }
.bbCodeBlock,
.hasJs .bbCodeSpoilerText {
	margin-right: 0;
	border-radius: 0;
	box-shadow: none;
}
.bbCodeBlock .type, .bbCodeBlock pre {
	border-radius: 0;
}
.mainContent { margin-right: 210px }
.sidebar { width: 200px }
.sidebar .visitorPanel .username {
	display: none;
}
.thread_view .threadAlerts .moderatedAlert {
	color: red;
}
}
