style.css:
@-moz-document regexp('https://www.baidu..*.wd=.*.') , url-prefix("http://news.baidu.com")
{
/*========= By www.wiibil.com========= */
*{
font-family:microsoft yahei!important;
}
a,a em{
/*     color:#4E4E4E!important; */
text-decoration:none!important;
}
a:hover{
text-decoration:none!important;
}


#wrapper_wrapper{
background:#efefef;
}

#head.s_down{
    box-shadow: 0 0 5px #efefef!important;
}

/* 百度产品 */
#content_right{
border-left:0px;
}
#s_tab{
color:white;
}

/* 内容区域 */
.container_s{
width:95%;
}
.leftBlock{
display:none;
}

/*纯文字单个搜索结果 */
#content_left{
padding-top:22px;
}
#content_left .result-op, #content_left .result{
border:1px solid rgba(0,0,0,.05);
padding:20px;
box-shadow: 0px 1px 1px #ebebeb;
background:white;
}

#content_left .result-op:hover,#content_left .result:hover{
box-shadow: 0 1px 3px 0 rgba(0,0,0,.08);
/* transform: translate3d(0, -1px, 0); */
-webkit-transition: all .5s linear;
transition: all .5s linear;
}
.c-border{
border: 1px solid #efefef!important;
    border-bottom-color:  #efefef!important;
    border-right-color: #efefef!important;
    box-shadow: none;
    -webkit-box-shadow: none;
}

.c-offset a,.c-offset a em,.result-op table tr td a,.result-op table tr td a em,.c-offset td a,.c-offset td a em,.op-tieba-general-lookmore a,.op-tieba-general-lookmore a em{
    color:#4E4E4E!important;
    text-decoration:none;
}

/* 搜索标题 */
.t a,p a,#rs a,#rs_top_new a,.hint_toprq_tips_items div a,.c-title a{
    color:#4E4E4E!important;
    text-decoration:none;
}
/* 新闻搜索 标题 */
.c-title{
font-size:18px;
    font-weight:normal;
}

.t a em,.c-title a em{
/*     color:#4E4E4E!important; */
    text-decoration:none;
}
.t a:hover,p a:hover,#rs a:hover,.res-gap-right16:hover,.c-abstract a:hover,.hint_toprq_tips_items div a:hover,.c-title a:hover{
color:#EE3182!important;
text-decoration:none;
}
.op-mapcity-btm a:hover{
color:white!important;
}
.t a:hover em,.c-title a:hover em{
color:#EE3182!important;
}

/* 介绍字体及颜色 */
.c-abstract,.c-abstract a,.op_offical_weibo_name,.op_offical_weibo_detail,.op_offical_weibo_pz a,.op_offical_weibo_other a,.c-row,.c-more_link,.op_tour_aim_tip span a,.op-koubei2-comt,.op-koubei2-more a,.op_weather4_twoicon_aa,.op_weather4_twoicon_close, .op_weather4_twoicon_open,.op_sp_realtime_bigpic5_list_info a,.op_sp_realtime_bigpic5_subTitle_con a,.op_sp_realtime_bigpic5_first_abs a{
    color:#98a1a1!important;
    font-size: 12px;
    text-decoration:none;
}

/* 搜索结果标红 */
em,.c-row em,.c-abstract em,.c-offset a em,.result-op table tr td a em,.c-offset td a em,.op-tieba-general-lookmore a em,.c-gap-top-small a:hover,.opr-recommends-merge-more-btn:hover,.op_sp_realtime_bigpic5_first_abs a:hover{
color:#EE3182!important;
}


.c-gap-left{
margin-left:0px;
}
.c-offset{
padding-left:0px;
}



/* 评价 */
.c-pingjia{
display:none;
}

/* 百度快照*/
.f13 .m{
    color:#98a1a1!important;
    text-decoration:none;
}
.c-tip-con .c-tip-menu li a{
color:#4E4E4E;
}

.c-text{
display:none;
}
/* 百度认证 百度推荐*/
.c-trust-as,.vstar,.c-trust,.op_LAMP,.c-recommend,.se_common_hint,.c-icon-bear-border{
display:none!important;
}

/* 相关搜索 */
#rs{
padding:20px;
    width:538px!important;
}

/* 搜索页码 */
#page .fk{
display:none;
}
#page a, #page strong{
    color:#4E4E4E;
height:36px;
}
.pc{
color:#4E4E4E;
}

/* 右侧边栏 */
#content_right{
margin:20px;
background:white;
}
.container_s #content_right:hover{
box-shadow: 0 10px 10px rgba(0,0,0,0.1);
/* transform: translate3d(0, -1px, 0); */
-webkit-transition: all .5s linear;
transition: all .5s linear;
}

/* 边栏标题 */
.cr-title{
padding-top:20px;
}
.cr-title,.cr-title-sub a{
font-weight:normal;
color:#4E4E4E;
}
/* text-decoration:none; */

/* 边栏描述文字 */
.c-abstract, .c-abstract a, .c-gap-top-small,.c-gap-top-small a, .cr-title-sub,.opr-recommends-merge-more-btn{
    color:#98a1a1!important;
    font-size: 10px;
    text-decoration:none;
}

/* 网址部分 */
.f13 a,.c-showurl,.g{
color:#84b15f!important;
font-size: 12px!important;
}

/* 新闻搜索返回网页搜索 */
#gotoPage{
   width:1px!important;
    height:1px!important;
    overflow:hidden;
}

/*========= Ad Block ========= */

div[style*="visibility:visible"] {
    display: none !important;
    visibility: hidden !important;
    opacity: 0;
    height: 0px;
    overflow: hidden;
}
}

