}
.s-skin-hasbg .s-top-wrap{
background:rgba(0,0,0,.2);
}
.s-mod-weather  .polution-level-10 .show-polution-name{
color:white;
font-weight: normal;
}
.s-isindex-wrap a,.user-name,#s_usersetting_top span,.s-icons a{
font-weight:normal!important;
text-decoration:none!important;
}
.s-mod-weather .show-icon .weather-icon,.s-treasure{
display:none!important;
}
.s-skin-hasbg #kw:hover{
box-shadow: 0 0 8px 0 rgba(0,0,0,0.2), 0 0 0 1px rgba(0,0,0,0.1);
transition:box-shadow .2s;
}
#form .bdsug{
top:44px!important;
    border-radius:3px;
}
}
