style.css:
@-moz-document regexp('http://www\.infoq\.com/cn/articles/.*') {
/* 隐藏 wrapper背景图片 */
#wrapper {
    background-image:none!important;
}

/* 隐藏 topInfo */
#topInfo {
    display: none !important;
}

/* 隐藏 header */
#header {
    display: none !important;
}

/* 隐藏 footer */
#footer {
    display: none !important;
}

/* 隐藏 bottomContent */
.bottomContent {
    display: none !important;
}

/* 隐藏 标题图片 */
#content .title_canvas img {
    display: none !important;
}

/* 隐藏 +1标志 */
#content .author_general em, #content .author_general em ~ *{
    display: none !important;
}

/* 隐藏 分享栏 */
#content .sh_t {
    display: none !important;
}

/* 隐藏 右侧栏目 */
#content .article_page_right {
    display: none !important;
}

/* 因为元素已经使用了display: block ! important; */
/* 所以这里使用绝对定位来移除元素 */
a[title="SumoMe"] {
    position: absolute;
    left: -9999px;
    top: -9999px;
}

/* 隐藏 InfoQ每周精要 */
#content .newsletter {
    display: none !important;
}

/* 隐藏 ab_detect */
.ab_detect {
    display: none !important;
}

/* 隐藏 text_info_article 的所有 div 子节点 */
.text_info_article > div {
    display: none !important;
}

/* 隐藏 text_info_article 的最后一个 p 节点(投稿说明) */
.text_info_article > p:last-of-type {
    display: none !important;
}
}
