style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rockpapershotgun.com") {

  /* global */
  body {
    background: #f0f0f0 !important;
    font-family: "Georgia" !important;
  }
  
  /* remove major elements */
  #skyscraper, #sidebar, #header, #footer, .leadersboards, .top-features 
    {display: none !important;}
  
  /* all containers set to same size and background */
  #page, #content, div#content div.content {
    width: 860px !important;
    background: #f0f0f0 !important;
  }

  /* articles without borders */
   #content, .content, article {border: none !important;}
   article {
    border-bottom: 2px solid #cecece !important;
    margin-bottom: 32px !important;
    padding-bottom: 16px !important;
  }
  .article-footer {margin-top: 48px !important;}
    
  /* see-through images (appear on mouse-over) */
  img {opacity: 0.0;}
  img:hover {opacity: 1.0;} 
  iframe {display: none !important}

  /* article crud */
  .featured-block-title {display: none !important;}
  #rps-video-carousel, #rps-article-carousel {display: none !important;}
  .social-buttons, .disclaimer {display: none !important;}
  .low-leaderboard-container {display: none !important;}
  
  /* comments section */
  #comments {border: none !important;}
  #all-comments {background: #f0f0f0 !important;}
  #comments ul.children li {background: #e7e7e7 !important;}
  .avatar-container {background: #bc575a; min-height: unset !important;}
  .avatar-container a {display: none !important;}
  
  /* text */
  article {
    line-height: 160% !important;
    font-size: 18px !important;
  }
  
  h1, h2, h3 {font-family: Tahoma !important;}
  h1 {font-size: 26px !important;}
  .byline {font-style: italic !important;}
  nav, .pagination, .post-navigation, .tags, .article-footer .comments, 
  .reply, .navigations 
    {font-family: Tahoma !important;}
  
  div.content div#comments .block p {
    line-height: 130% !important;
    font-size: 15px !important;
  }
  
  blockquote {
    background: #e7e7e7 !important;
    margin: 32px 0 !important;
  }
  
  
}
