/*Import*/

#wrapper.group {
	max-width: 100vw;
}
body[itemtype="http://schema.org/Article"] {
	background-color: #011e32;
	background-size: cover;
	background-attachment: fixed;
	background-position: center;
	background-repeat: no-repeat;
	/* Check more backgrounds at colourlovers.com */
  	/*[[bgimage]]*/
}
div.announce {
	border-radius: 2px;
	color: #fff !important;
	font-weight: 400;
	float: left;
	width: calc(33.333% - 20px);
	font-size: .8em;
	line-height: 1.2em;
	height: 60px;
	opacity: .9;
	margin-top: -60px;
    margin-top: 0;
}
div.body.group.announce:last-child {
	margin-bottom: 20px !important;
}
.announce a:link,
.announce a:visited {
	color: #fff;
	transition: all 300ms ease-in-out 0s;
	text-shadow: 0px 0px 2px transparent;
}
.announce:hover a:link {
	text-shadow: 1px 1px 1px #f19c7e;
	transition: all 300ms ease-in-out 0s;
	text-decoration: underline;
}
div.announce:nth-of-type(even) {
	background-color: hsl(347, 73%, 60%);
}
div.announce:nth-of-type(odd) {
	background-color: hsl(315, 55%, 32%);
}
strong {
	color: #4e4e4e;
}
.box .body {
	background: linear-gradient(to right, hsla(0,0%,94%,0.7) 0%,hsla(0,0%,95%,0.7) 25%,hsla(0,0%,100%,0.25) 100%);
	box-shadow: 1px 1px 3px rgba(0,0,0,0.3);
}
#main-article .article {
	background-color: rgba(255,255,255,.8);
	border-radius: 2px;
	box-shadow: 0 0 3px rgba(0,0,0,.7);
}
#main-article .article .info {
	padding: 40px 40px;
}
div#body.smooth .col-12 .box {
	width: 22.999%;
    width: auto;
	float: right;
}
div#body.smooth .col-12 .box .patreon {
	font-size: .8em;
	line-height: normal;
	border-radius: 2px;
	background-color: rgba(255,255,255,.8);
	box-shadow: 0 0 3px rgba(0,0,0,.7);
}
.articles .article {
	margin-top: 25px;

	background-color: rgba(239, 239, 239, .7);
	box-shadow: 0 0 3px rgba(0,0,0,.7);
	border-radius: 2px;
}
.articles .article .image a img {
	margin-bottom: -6px;
	border-top-left-radius: 2px;
	border-bottom-left-radius: 2px;
}
.article .info .title {
	font-size: 1.4em;
	line-height: 1em;
}
