.article .tags ul li.ea,
.article .tags ul li {
	float: right;

	margin: 0 0 0 17px;
	padding: 0 5px 0 0;

	transition: background-color 300ms ease-in-out 100ms;

	border-top-right-radius: 2px;
	border-bottom-right-radius: 2px;
	background-color: #9baebd;
	box-shadow: 1px 1px 1px rgba(0,0,0,.3);
