.article .tags ul li:hover {
	transition: background-color 300ms ease-in-out 100ms;

	background-color: #428bca;
}
.article .tags ul li.ea:before,
.article .tags ul li:before {
	position: absolute;

	float: left;

	margin-left: -10px;

	content: '';
	transition: border-color 300ms ease-in-out 100ms;

	border-top: 9px solid transparent;
	border-right: 10px solid #9baebd;
	border-bottom: 9px solid transparent;
}
.article .tags ul li.ea:before {
	border-right: 10px solid #ca8142;
}
.article .tags ul li.ea:hover:before,
.article .tags ul li:hover:before {
	transition: border-color 300ms ease-in-out 100ms;

	border-right: 10px solid #428bca;
}
.article .tags ul li.ea a,
.article .tags ul li a {
