style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slashdot.org") {
    body {
        background-color: #073642;
        color: #839496;
    }
    body > section {
        background-color: #002b36;
    }
    #d2header {
        background-color: #073642;
    }
    #slashboxes article, #firehose article, #comments article,
    .commentBody {
        color: #839496;
    }
    a, .link, a.link {
        color: #268bd2;
    }
    a:visited, a:hover {
        color: #6c71c4;
    }
    h3, aside h3,
    #firehose article header .story-title a,
    #firehose article header h2, #firehose article header h2 a,
    body.slashdot-it li.comment.full h4 a, #comments .full h4,
    #comments .full h4 a, #commentlisting.d1 h4,
    #wide #commentlisting h4 a, #commentlisting.d1 h4 a {
        color: #eee8d5;
    }
    body.slashdot-it li.comment.full .details, #comments .details {
        background-color: #073642;
        color: #839496;
    }
    .slashdot-it #commentlisting li.comment.full, #comments .full {
        border-color: #073642;
    }
    #firehose article > p i, article .quote, #editpreview article > p i, .commentBody .quote {
        border-color: #839496;
    }

    section#besttabs {
        background-color: #002b36;
        color: #839496;
    }
    ul.besttabslist li.active {
        background-color: #268bd2;
    }
    ul.besttabslist li.active, ul.besttabslist li.active a, ul.besttabslist li.active a:hover {
        color: #002b36;
    }

    .poll-group-form label {
        background-color: #073642;
    }
    .poll-group-form label:hover {
        background-color: #073642;
    }
    .poll-bar-bg {
        background-color: #073642;
    }
    .poll-bar-bg .poll-bar-fill {
        background-color: #6c71c4;
    }
    .poll-bar-bg .poll-bar-text {
        color: #fdf6e3;
    }
    #slashboxes article {
        background-color: #073642;
        border-color: #073642;
    }
}
