style.css:
@-moz-document url("http://gabrielecirulli.github.io/2048/") {
body {
    background-color: #1b1b1d;
    background: #1b1b1d;
    color:  #CFD8DC;
}

html, body {
    background-color: #1b1b1d;
}

a {
    color: #E3F2FD;
}

.score-container .score-addition, .best-container .score-addition {
    color: #263238;
}

.game-message.game-over {
    background: rgba(78, 100, 111, 0.88);
}

.game-message a {
    background: #757575;
}

.input {
    background: #757575;
}

h1.title {
    color: #FFFFFF;
}

.grid-cell {
    background: #9E9E9E;
}

.game-container {
    background: #757575;
}

.pp-donate button {
	background: #757575;
}

.btc-donate {
    background: #757575;
}

.score-container, .best-container, .restart-button {
    background: #757575;
}

.score-container:after, .best-container:after {
    color: #CFD8DC;
}


.tile.tile-2048 .tile-inner {
    background: #006064;
}

.tile.tile-1024 .tile-inner {
    background: #263238;
}

.tile.tile-512 .tile-inner {
    background: #37474F;
}

.tile.tile-256 .tile-inner {
    background: #455A64;
}

.tile.tile-128 .tile-inner {
    background: #546E7A;
}

.tile.tile-64 .tile-inner {
    background: #607D8B;
}
                                            
.tile.tile-32 .tile-inner {
    background: #78909C;
}

.tile.tile-16 .tile-inner {
    background: #90A4AE;
}

.tile.tile-8 .tile-inner {
    background: #B0BEC5;
}

.tile.tile-4 .tile-inner {
    background: #CFD8DC;
    color: #263238;
}

.tile.tile-2 .tile-inner {
    background: #ECEFF1;
    color: #263238;
}

.tile.tile-128 .tile-inner {
    box-shadow: 0 0 30px 10px #7192a1, inset 0 0 0 1px #7192a1;
}

.tile.tile-256 .tile-inner {
    box-shadow: 0 0 30px 10px #5d7885, inset 0 0 0 1px #5d7885;
}

.tile.tile-512 .tile-inner {
    box-shadow: 0 0 30px 10px #4e646f, inset 0 0 0 1px #4e646f;
}

.tile.tile-1024 .tile-inner {
    box-shadow: 0 0 30px 10px #3b4d56, inset 0 0 0 1px #3b4d56;
}

.tile.tile-2048 .tile-inner {
    box-shadow: 0 0 30px 10px #008287, inset 0 0 0 1px #008287;
}
}
