style.css:
@-moz-document domain("youtube.com") {
/*Background*/

body {
    background: #15151d;
}
/*Main Section*/

.yt-card {
    margin: 0 0 10px;
    border: 1px;
    background: #181821;
    box-shadow: 0px 0px 5px 0px rgb(0, 0, 0);
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    border-style: solid;
    border-color: #252538;
}
/*Main Section Bottom Border*/

#browse-items-primary .item-section>li>.yt-lockup-tile,
.branded-page-v2-primary-col .branded-page-box,
.section-list li .item-section .branded-page-box {
    border-bottom: 1px solid #252538;
}
/*Left Section*/

#appbar-guide-menu,
.guide-flyout {
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: #181821;
    z-index: 1999999999;
}
/*Left Sect Right Border*/

.guide-pinned .guide-pinning-enabled #appbar-guide-menu {
    border-right: 1px solid #252538;
    box-shadow: none;
}
/*Home, Trending, Subscriptions Background*/

#masthead-appbar {
    position: relative;
    background-color: #181821;
    border-bottom: 1px solid #252538;
    height: 40px;
    line-height: 40px;
}
/*Main Section Background Text*/

.yt-ui-ellipsis {
    background-color: #181821;
    display: block;
    line-height: 1.3em;
    overflow: hidden;
    position: relative;
    text-overflow: ellipsis;
    white-space: normal;
    word-wrap: break-word;
}
/*Top Section*/

#yt-masthead-container {
    background: #181821;
    border-bottom: 1px solid #252538;
    min-width: 0;
    padding-bottom: 8px;
    padding-top: 7px;
    position: relative;
}
/*YouTube Usernames*/

.comment-author-text {
    color: #ccb1ff;
    font-weight: 500;
    text-decoration: none;
    word-break: break-all;
    background: url(http://i.imgur.com/gU8WjjV.gif);
}
/*YouTube Titles Main Page*/

.yt-lockup .yt-lockup-meta a,
.yt-lockup .yt-lockup-description a,
.yt-lockup .yt-lockup-byline a {
    color: #7b7494;
}
.yt-lockup-title a,
.yt-lockup:hover a,
.yt-lockup:hover .yt-lockup-meta a,
.yt-lockup:hover .yt-lockup-description a {
    color: #8888b1;
}
/*YouTube Usernames Main Page*/

.branded-page-module-title a:visited,
.branded-page-module-title a {
    color: #8f8fc1;
}
/*Comments*/

hr,
html,
i,
iframe,
img,
ins,
kbd,
label,
legend,
li,
menu,
object,
ol,
p,
pre,
q,
s,
samp,
small,
span,
strike,
strong,
sub {
    margin: 0;
    padding: 0;
    border: 0;
    font-size: 100%;
    background: transparent;
    color: #a3a3b5;
}
/*Left Section Active Highlight*/

.guide-item.guide-item-selected,
.guide-item.guide-item-selected:hover,
.guide-item.guide-item-selected .yt-deemphasized-text,
.guide-item.guide-item-selected:hover .yt-deemphasized-text {
    background: #252538;
    color: #fff;
}
/*Cinema View*/

.watch-stage-mode #theater-background {
    background-color: #121219;
    background-image: repeating-linear-gradient(120deg, rgba(255, 255, 255, .1), rgba(255, 255, 255, .1) 1px, transparent 1px, transparent 60px), repeating-linear-gradient(60deg, rgba(255, 255, 255, .1), rgba(255, 255, 255, .1) 1px, transparent 1px, transparent 60px), linear-gradient(60deg, rgba(0, 0, 0, .1) 25%, transparent 25%, transparent 75%, rgba(0, 0, 0, .1) 75%, rgba(0, 0, 0, .1)), linear-gradient(120deg, rgba(0, 0, 0, .1) 25%, transparent 25%, transparent 75%, rgba(0, 0, 0, .1) 75%, rgba(0, 0, 0, .1));
    background-size: 70px 120px;
    box-shadow: inset 0px 0px 10px 0px rgba(0, 0, 0, 0.75);
    border-bottom: solid #363652;
    border-width: 1px;
}
/*Search Bar*/

#masthead-search-terms {
    background-color: #121219;
    font-size: 14px;
    height: 29px;
    line-height: 30px;
    margin: 0 0 2px;
    overflow: hidden;
    position: relative;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    -moz-transition: border-color .2s ease;
    -webkit-transition: border-color .2s ease;
    transition: border-color .2s ease;
}
/*Search Bar Highlight*/

.gsfe_a {
    border: 1px solid #252538;
    border-top-color: #252538;
    box-shadow: inset 0px 1px 2px rgba(0, 0, 0, 0.1);
    -moz-box-shadow: inset 0px 1px 2px rgba(0, 0, 0, 0.1);
    -webkit-box-shadow: inset 0px 1px 2px rgba(0, 0, 0, 0.1);
}
.masthead-search-terms-border {
    border: 1px solid #181821;
    box-shadow: inset 0 1px 2px #181821;
}
/*Search Button*/

.yt-uix-button-default,
.yt-uix-button-default[disabled],
.yt-uix-button-default[disabled]:hover,
.yt-uix-button-default[disabled]:active,
.yt-uix-button-default[disabled]:focus {
    border-color: #252538;
    background: #252538;
    color: #252538;
}
/*Search Text*/

body,
input,
button,
textarea,
select {
    font-family: Roboto, arial, sans-serif;
    font-size: 13px;
    color: #a3a3b5;
}
.gssb_m {
    color: #000;
    background: #121219;
    border: 1px solid #252538;
    border-top-color: #252538;
}
/*Like Bar*/

.video-extras-sparkbar-likes {
    float: left;
    height: 2px;
    background: #252538 url(http://i.imgur.com/gU8WjjV.gif);
}
/*YouTube Video Search bar*/

.ytp-swatch-background-color,
.ytp-swatch-background-color-secondary {
    background: #121219 url(http://i.imgur.com/gU8WjjV.gif);
}
.ytp-progress-list {
    z-index: 39;
    background: rgb(65, 65, 95);
    height: 100%;
    -moz-transform: scaleY(0.6);
    -ms-transform: scaleY(0.6);
    -webkit-transform: scaleY(0.6);
    transform: scaleY(0.6);
    -moz-transition: -moz-transform .1s cubic-bezier(0.4, 0.0, 1, 1);
    -webkit-transition: -webkit-transform .1s cubic-bezier(0.4, 0.0, 1, 1);
    -ms-transition: -ms-transform .1s cubic-bezier(0.4, 0.0, 1, 1);
    transition: transform .1s cubic-bezier(0.4, 0.0, 1, 1);
    position: relative;
}
/*YouTube Subscribe Button*/

.yt-uix-button-subscribe-branded,
.yt-uix-button-subscribe-branded[disabled],
.yt-uix-button-subscribe-branded[disabled]:hover,
.yt-uix-button-subscribe-branded[disabled]:active,
.yt-uix-button-subscribe-branded[disabled]:focus {
    color: #fefefe;
    background-color: #2d2d44;
}
/*Hover*/

.yt-uix-button-subscribe-branded:hover {
    background: #252538 url(http://i.imgur.com/gU8WjjV.gif);
}
/*Comment Box*/

.comment-simplebox {
    background-color: #15151d;
    overflow: hidden;
    display: none;
    position: relative;
}
.comment-simplebox-frame {
    border: 1px solid #15151d;
    border-radius: 2px;
    box-shadow: inset 0 0 1px rgba(0, 0, 0, .05);
    margin-left: 11px;
    margin-bottom: 8px;
    padding: 10px;
    min-height: 28px;
}
.comment-simplebox-text,
.comment-simplebox-prompt {
    background-color: #252538;
    color: #a3a3b5;
    line-height: 16px;
    text-align: left;
}
.comment-simplebox:not(.yt-uix-form-error).focus .comment-simplebox-frame {
    border-color: #252538;
    box-shadow: inset 0 0 1px rgba(0, 0, 0, .1);
}
.comment-simplebox-renderer-collapsed-content {
    border: 1px solid #252538;
    color: #b8b8b8;
    cursor: pointer;
    margin-left: 11px;
    min-height: 28px;
    border-radius: 0px;
    padding: 10px;
}
/*Youtuber*/

#watch-header .yt-user-info a {
    display: inline-block;
    height: 22px;
    color: #7b7494;
    font-weight: 500;
    max-width: 315px;
    overflow: hidden;
    text-overflow: ellipsis;
    vertical-align: top;
    -webkit-animation-name: blinker;
    -webkit-animation-duration: 3s;
    -webkit-animation-timing-function: linear;
    -webkit-animation-iteration-count: infinite;
    -moz-animation-name: blinker;
    -moz-animation-duration: 1s;
    -moz-animation-timing-function: linear;
    -moz-animation-iteration-count: infinite;
    animation-name: blinker;
    animation-duration: 1.5s;
    animation-timing-function: linear;
    animation-iteration-count: infinite;
}
@-webkit-keyframes blinker {
    from {
        opacity: 1.0;
    }
    to {
        opacity: 0.0;
    }
}
.blink {
    text-decoration: blink;
    -webkit-animation-name: blinker;
    -webkit-animation-duration: 0.6s;
    -webkit-animation-iteration-count: infinite;
    -webkit-animation-timing-function: ease-in-out;
    -webkit-animation-direction: alternate;
}
/*Video List Text*/

.video-list .video-list-item .title {
    color: #a3a3b5;
    font-size: 14px;
    font-weight: 500;
}
/*Highlight*/

#watch7-sidebar .video-list-item a:hover .title,
#watch7-sidebar .video-list-item a:hover .title .yt-deemphasized-text {
    color: #66668a;
}
#watch7-sidebar .video-list-item:hover .title,
#watch7-sidebar .video-list-item:hover .title .yt-deemphasized-text {
    color: #66668a;
    text-decoration: none;
}
/*Bottom Section*/

body #footer-container {
    margin-top: -145px;
    background-color: #181821;
    border-top: 1px solid #252538;
}
#footer-main {
    border-bottom: 1px solid #181821;
    padding-bottom: 15px;
}
/*miscellaneous*/

#watch8-action-buttons {
    position: relative;
    padding-top: 5px;
    border-top: 1px solid #181821;
}
a.yt-uix-button-epic-nav-item.partially-selected:hover,
button.yt-uix-button-epic-nav-item:hover,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item:hover,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading {
    height: 29px;
    line-height: 29px;
    border-bottom: 3px solid;
    border-color: #252538;
    padding-bottom: 0;
    display: inline-block;
}
a.yt-uix-button-epic-nav-item.selected,
a.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
button.yt-uix-button-epic-nav-item.selected,
button.yt-uix-button-epic-nav-item.yt-uix-button-toggled,
.epic-nav-item.selected,
.epic-nav-item.yt-uix-button-toggled,
.epic-nav-item-heading {
    color: #a3a3b5;
    font-weight: 500;
}
a.yt-uix-button-epic-nav-item:hover,
a.yt-uix-button-epic-nav-item.selected,
a.yt-uix-button-epic-nav-item.yt-uix-button-toggled {
    height: 29px;
    line-height: 29px;
    border-bottom: 3px solid;
    border-color: #252538;
    padding-bottom: 0;
    display: inline-block;
}
.yt-subscription-button-subscriber-count-branded-horizontal,
.yt-subscription-button-subscriber-count-unbranded-horizontal {
    display: none;
    margin-left: -2px;
    border: 1px solid #252538;
    background-color: #252538;
    vertical-align: middle;
    border-radius: 0px;
}
.yt-uix-button-subscribed-branded,
.yt-uix-button-subscribed-branded[disabled],
.yt-uix-button-subscribed-branded[disabled]:hover,
.yt-uix-button-subscribed-branded[disabled]:active,
.yt-uix-button-subscribed-branded[disabled]:focus,
.yt-uix-button-subscribed-unbranded,
.yt-uix-button-subscribed-unbranded[disabled],
.yt-uix-button-subscribed-unbranded[disabled]:hover,
.yt-uix-button-subscribed-unbranded[disabled]:active,
.yt-uix-button-subscribed-unbranded[disabled]:focus {
    background-color: #252538;
    color: #666;
    border-radius: 0px;
}
li.guide-section h3 a {
    width: 155px;
    color: #c2c2d6;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    display: block;
}
.yt-uix-button-subscribed-branded,
.yt-uix-button-subscribed-branded[disabled],
.yt-uix-button-subscribed-branded[disabled]:hover,
.yt-uix-button-subscribed-branded[disabled]:active,
.yt-uix-button-subscribed-branded[disabled]:focus,
.yt-uix-button-subscribed-unbranded,
.yt-uix-button-subscribed-unbranded[disabled],
.yt-uix-button-subscribed-unbranded[disabled]:hover,
.yt-uix-button-subscribed-unbranded[disabled]:active,
.yt-uix-button-subscribed-unbranded[disabled]:focus {
    border: 1px solid #252538;
    color: #666;
}
.yt-card .yt-uix-button-expander {
    width: 100%;
    text-transform: uppercase;
    color: #767676;
    border-top: 1px solid #181821;
    box-shadow: none;
}
.comment-simplebox-renderer {
    padding: 0 0 20px;
    margin-bottom: 20px;
    border-bottom: 1px solid #181821;
}
.comment-simplebox-arrow .arrow-inner {
    border: none;
    left: 2px;
    top: 1px;
    z-index: 1;
}
.comment-simplebox-arrow .arrow-outer {
    border: none;
    left: 0;
    top: 0;
}
.comment-renderer-author-comment-badge.creator {
    background-color: #3c3c5a;
    border-radius: 0px;
    padding: 1px 6px;
    vertical-align: middle;
}
#action-panel-details a {
    color: #71627b;
}
#action-panel-details:hover a {
    color: #8d739c;
}
.comment-renderer-header .comment-renderer-time a {
    color: #8c879e;
}
#watch7-sidebar .watch-sidebar-separation-line {
    border-bottom: 1px solid #252538;
    margin: 0 5px 15px;
}
/*Icons & Logo*/

.addto-button:before {
    background: no-repeat url(http://i.imgur.com/2fZKkHs.png) -351px -117px;
    background-size: auto;
    width: 20px;
    height: 20px;
}
.action-panel-trigger-share:before {
    background: no-repeat url(http://i.imgur.com/gXvaLKw.png) -182px -42px;
    background-size: auto;
    width: 20px;
    height: 20px;
}
#action-panel-overflow-button:before,
.action-panel-trigger-overflow:before {
    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) -42px -184px;
    background-size: auto;
    width: 20px;
    height: 20px;
}
.like-button-renderer-like-button:before {
    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) -375px -54px !important;
    background-size: auto;
    width: 20px;
    height: 20px;
}
.like-button-renderer-dislike-button:before {
    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) -628px -294px;
    background-size: auto;
    width: 20px;
    height: 20px;
    padding-bottom: 1px;
}
#yt-masthead #logo-container .logo {
    margin-right: 5px;
    background: no-repeat url(http://i.imgur.com/52nCfh3.png) -98px -1047px;
    background-size: auto;
    width: 72px;
    height: 30px;
}
.autoplay-bar .autoplay-info-icon {
    cursor: pointer;
    margin-left: 4px;
    margin-right: 4px;
    margin-top: -1px;
    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) -263px -190px;
    background-size: auto;
    width: 16px;
    height: 16px;
}
#yt-masthead-user .yt-uix-button-icon-bell {
    left: 0;
