    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) -895px -230px;
    background-size: auto;
    width: 30px;
    height: 30px;
}
.yt-uix-button-icon-view-module {
    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) -298px -69px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
.yt-uix-button-icon-view-list {
    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) -895px -306px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
.yt-uix-button-icon-material-upload {
    background: no-repeat url(http://i.imgur.com/ml3Zkjb.png) 0 -75px;
    background-size: auto;
    width: 24px;
    height: 24px;
}
.yt-uix-form-input-select,
.yt-uix-form-input-text,
.yt-uix-form-input-textarea {
    background: #181821;
    border-color: #252538;
}
.yt-uix-checkbox-on-off input[type=checkbox]:checked+label {
    background-color: #363652;
}
.yt-card .yt-uix-tabs {
    margin-bottom: 15px;
    border-bottom: 1px solid #181821;
}
.webkit .yt-ui-ellipsis-2 {
    -webkit-line-clamp: 2;
    color: #8585a0;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    border-radius: 10px;
    background-color: #252538;
}
::-webkit-scrollbar {
    width: 12px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
