}
.yt-alert-default.yt-alert-info,
.yt-alert-actionable.yt-alert-info,
.yt-alert-naked.yt-alert-info .yt-alert-icon,
.yt-alert-small.yt-alert-info {
    background: #252538;
}
.channel-header .secondary-header-contents {
    position: relative;
    padding: 0 15px;
