}
#player-playlist .playlist-videos-list li:hover .yt-ui-ellipsis {
    background-color: #1f1f2f;
}
#player-playlist .control-bar {
    border-top: 1px solid #252538;
    height: 24px;
    padding: 7px 5px 8px;
}
.yt-uix-menu-trigger-selected .yt-ui-menu-item,
.yt-ui-menu-item:hover {
    background: #181821;
}
.add-to-widget .addto-playlist-item:hover,
.add-to-widget .create-playlist-item:hover,
.add-to-widget .addto-playlist-item.yt-uix-kbd-nav-highlight {
    background-color: #181821;
}
.add-to-widget .create-playlist-item {
    display: none;
    width: 100%;
    text-align: left;
    padding: 5px 0 5px 16px;
    margin: 10px 0;
    color: #625577;
    white-space: nowrap;
    cursor: pointer;
}
.watch-playlist {
    background: #181821;
    color: #b8b8b8;
    margin-bottom: 10px;
    position: relative;
    border: #252538;
    border-style: solid;
    border-width: 1px;
}
}
