style.css:
@-moz-document domain("youtube.com") {

.html5-scrubber-button, .ytp-scrubber-button {
   background: radial-gradient(ellipse at center, #aaaaaa 25%,#fafafa 50%) !important;
   width: 16px !important;
   height: 16px !important;
   left: 0px !important;
   border: none !important;
   margin-top: 1px !important;
}
   
.html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list {
   height: 10px !important;
}
.html5-progress-bar-container, .ytp-progress-bar-container {
   margin-bottom: -3px;
}
   
.ytp-volume-slider-track {
   background: #656565 !important;
}
   
.ytp-chrome-controls {
   background: linear-gradient(to bottom, #fafafa 5%, #d2d2d2 100%);
   height: 30px;
   line-height: 30px !important;
   opacity: 0.85 !important;
   transition: background 1s ease !important;
}

.ytp-scrubber-container{
   left: -1px !important;  
}
.ytp-chrome-bottom {
   height: 30px;
   line-height: 30px;
}
   
.ytp-big-mode .ytp-chrome-bottom{
   height: 48px;
   line-height: 48px;
}
   
.ytp-time-display{
   line-height: 30px;
}
   
.ytp-autohide .ytp-chrome-top-buttons, .ytp-autohide .ytp-chrome-bottom, .ytp-chrome-top[aria-hidden="true"], .ytp-chrome-bottom[aria-hidden="true"] {
   opacity: 1 !important;
   transition: opacity 1s cubic-bezier(0.455, 0.03, 0.515, 0.955) !important;
   transition-delay: 0.4s !important;
}

.ytp-autohide .ytp-ad-persistent-progress-bar-container{
   transition: all 0.5s cubic-bezier(0.455, 0.03, 0.515, 0.955) !important;
   transition-delay: 0.5s !important;      
}

 .ytp-autohide .ytp-chrome-bottom {
   opacity: 1;
   left: 0px !important;
   width: 100% !important;
   transition: opacity 0.5s cubic-bezier(0.455, 0.03, 0.515, 0.955), left 0.4s ease, width 0.4s ease !important;
   transition-delay: 0.3s !important;
}
   
.ytp-autohide .ytp-chrome-controls {
   opacity: 0 !important;
   transition: opacity 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}
   
.ytp-autohide .ytp-chrome-bottom .ytp-progress-bar-container{
   opacity: 0 !important;
   transition: opacity 3s cubic-bezier(0.8, 0, 1, 0.2), bottom 0.4s cubic-bezier(0.455, 0.03, 0.515, 0.955), height 0.4s cubic-bezier(0.455, 0.03, 0.515, 0.955) !important;
   height: 3px !important;
   bottom: 6px !important;
   transition-delay: 0.5s !important;
}
   
.ytp-autohide .ytp-chrome-bottom .ytp-chrome-controls path{
   color: transparent !important;
   fill: transparent !important;
   transition: all 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}
   
.ytp-autohide .ytp-chrome-bottom .ytp-chrome-controls .ytp-svg-fill{
   fill: transparent !important;
   transition: fill 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}
   
.ytp-autohide .ytp-chrome-bottom .ytp-chrome-controls .ytp-svg-shadow{
   stroke: transparent !important;
   transition: stroke 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}
   
.ytp-autohide .ytp-chrome-bottom .ytp-chrome-controls .ytp-time-display{
   color: transparent !important;
   transition: color 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}
   
.ytp-autohide .ytp-chrome-bottom .ytp-chrome-controls .ytp-time-separator{
   color: transparent !important;
   transition: color 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}
   
.ytp-autohide .ytp-chrome-bottom .ytp-chrome-controls .ytp-time-duration{
   color: transparent !important;
   transition: color 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}

.ytp-autohide .ytp-chrome-bottom .ytp-chrome-controls .ytp-hd-quality-badge{
   opacity: 0 !important;
   transition: opacity 0.7s cubic-bezier(0.8, 0, 1, 0.2) !important;
}
   
.ytp-gradient-top{
   background: transparent !important;
}
   
.ytp-gradient-bottom{
   background: linear-gradient(to top, #d2d2d2 0%, #fafafa 30px, transparent 30.1px);
   transition: opacity 0.4s cubic-bezier(0.455, 0.03, 0.515, 0.955) !important;
}
   
.ytp-big-mode .ytp-gradient-bottom{
   background: linear-gradient(to top, #d2d2d2 0%, #fafafa 48px, transparent 48.1px);
   transition: opacity 0.4s cubic-bezier(0.455, 0.03, 0.515, 0.955) !important;
}
   
   
.ytp-chrome-top, .ytp-chrome-bottom{
   text-shadow: none;    
}
   
.ytp-svg-fill {
	fill: #656565;
}

.ytp-chrome-controls path{
   fill: #656565;   
}

.ytp-volume-slider-handle{
   background: #BF382B;
}

.ytp-volume-slider-handle:before{
   background: #BF382B !important;
}
   
.ytp-volume-slider-handle:after{
   background: rgba(191,56,43,0.275) !important;
}
   
.ytp-volume-slider{
   color: #656565;
}
   
.ytp-time-display{
   color: #656565;    
}
   
.ytp-time-separator, .ytp-time-duration{
   color: #656565;  
}

.video-extras-sparkbar-likes{
   background: #27ae60; 
}

.video-extras-sparkbar-dislikes{
   background: #e53935; 
}
   
#watch8-sentiment-actions .like-button-renderer .yt-uix-button{
   background: #e7e7e7;
   transition: all 0.1s ease;
}
   
#watch8-sentiment-actions .like-button-renderer .yt-uix-button :active{
   background: #dedede;
   transition: all 0.1s ease;
}

#channel-subheader .yt-uix-button-content{
   font-size: 110% !important;  
   font-family: Roboto,arial,sans-serif !important;
}
   
#channel-subheader .epic-nav-item-heading {
   font-size: 110% !important;   
   color: #fafafa;
}
   
#appbar-nav .epic-nav-item-heading{
   border-top: none;
   border-bottom: none;
   border-left: 7px solid #757575;
   border-right: 7px solid #757575;
   border-radius: 2px;
   background: #757575 !important;
   font-weight: normal !important;
   font-size: 100% !important;  
   font-family: Roboto,arial,sans-serif !important;
   color: #fafafa;
}

.yt-uix-button-epic-nav-item.partially-selected:hover, button.yt-uix-button-epic-nav-item:hover, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item:hover, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading{
   border-top: 3px solid #757575;
   border-bottom: 3px solid #757575;
   border-left: 9px solid #757575;
   border-right: 9px solid #757575;
   border-radius: 3px;
   background: #757575 !important;
   font-weight: normal !important;
   margin-top: -2px;
}
   
a.yt-uix-button-epic-nav-item, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled{
   border-top: 2px solid #ffffff !important;
   border-bottom: 2px solid #ffffff !important;
   border-left: 6px solid #ffffff !important;
   border-right: 6px solid #ffffff !important;
   border-radius: 2px !important;
   transition: all 0.125s ease;
}
   
.yt-uix-button-epic-nav-item:hover, a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled{
   border-top: 2px solid #eeeeee !important;
   border-bottom: 2px solid #eeeeee !important;
   border-left: 6px solid #eeeeee !important;
   border-right: 6px solid #eeeeee !important;
   background: #eeeeee !important;
   transition: all 0.125s ease;
} 

.appbar-guide-menu-layout{
   left: -250px;
   visibility: hidden;
   transition: all 0.4s ease !important;
}

.content-snap-width-3 .flex-width-enabled.flex-width-enabled-snap .content-alignment{
   width: 80%;
}
   
#channel-search .yt-uix-button.yt-uix-button-epic-nav-item, button.yt-uix-button-epic-nav-item, .epic-nav-item {
	border: none !important;
	color: #666 !important;
	font-weight: normal;
	border-radius: 0 !important;
   border: none !important;
   background: transparent !important;
}
   
#channel-search .secondary-nav.yt-uix-button-epic-nav-item:hover, .secondary-nav.epic-nav-item:hover{
   border: none !important;
	color: #666 !important;
	font-weight: normal;
	border-radius: 0 !important;
   border: none !important;
   background: transparent !important;
   margin-top: 0px !important;
}
}
