style.css:
@-moz-document url-prefix("http://www.mspaintadventures.com/") {

/* Sara's Homestuck Style ♥ */

body {
	background-image: url("https://images.alphacoders.com/221/221971.jpg"); 
	background-attachment: fixed; }

td[width="7"], table[width="208"], td[height="7"], img[src^="http://cdn.mspaintadventures.com/images/WLFtopbanner"], [background="http://cdn.mspaintadventures.com/images/bannerframe.png"] { 
    display: none; }

td[width="950"] { 
    background: transparent; }

table[bgcolor="c6c6c6"] {
    background-color: #acc4b1; }

td[bgcolor="efefef"], table[bgcolor="efefef"] {
    background-color: #acb1c4; }

p[style$="color:#000000"] {
    color: #0a0a0a !important; }

[bgcolor="#EEEEEE"] {
    background-color: #eeeeee !important; }

[bgcolor="5a5a5a"] {
    background-color: transparent !important; }

/* The End! ★ */
}
