style.css:
@-moz-document domain(wccftech.com) {
header {
    background-color: #111;
    border-bottom: 1px solid #444;
}
header .logo {
    filter: invert(95%) saturate(2) contrast(0.9) hue-rotate(330deg);
}
.nav li a {
	color: #ccc;
}
.nav li a:hover, .nav li a.active {
    color: #777;
}
body {
    background-color: #191919;
    color: #191919;
}
style + div > div, div.banner-ad + div > div, div.banner-ad + span > span {
    display:none !important;
}
.above-the-fold {
    background: #191919;
}
.content {
    background: #191919;
    padding: 30px;
}
.post {
    background: #222;
}
.topics a:hover {
    color: #b00000;
}
a:hover,
.featured-1 h2,
.featured-2 h2,
.text h2,
.sticky li a {
    transition: all 0.25s ease;
}
.headroom--pinned {
	box-shadow: 0px 0px 0px rgba(0,0,0,0);
}
a {
    color: #c9c9c9;
}
.sticky li.first {
	background: #111;
}
.sticky li.first a {
	color: #ccc;
}

.trending-posts li {
    border-bottom: 1px solid #252525;
}
.shares:hover,
.sohail-share {
    background: #1f2529;
}
.sticky-hardware h2,
.sticky-gaming h2,
.sticky-mobile h2 {
    text-align: center;
}
h1, h2, h3, h4, h5, h6 {
	color: #bcbcbc;
}
.sticky h2 span {
    border-radius: 4px;
    font-weight: 500;
    background: #101010;
    padding: 2px 10px 0px 10px;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-hardware > h2,
body > aside.sticky.wrap > section.sticky-hardware > h2,
body > main > aside:nth-child(2) > div > section.sticky-hardware > h2 {
    border-color: #ff543a;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-gaming > h2,
body > aside.sticky.wrap > section.sticky-gaming > h2,
body > main > aside:nth-child(2) > div > section.sticky-gaming > h2 {
    border-color: #45d87c;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-mobile > h2,
body > aside.sticky.wrap > section.sticky-mobile > h2,
body > main > aside:nth-child(2) > div > section.sticky-mobile > h2 {
    border-color: #0fa6f3;
}
.aside, .aside-deals, .aside-finance, .democracy {
    background-color: #1e1e1e;
    border: 1px solid #777;
}
.aside-finance .more {
    border: 2px solid #c9c9c9;
}
.dem-poll-title {
    color: #aaa;
}
p.wccf_related_post{
    background-color: #333;
}
.sidebar section h3 span {
    background: #333;
    color: #ffffff;
}
.sidebar section h3 {
    border-bottom: 2px solid #333;
}
.exclusives h2 span {
    background: #ffffff;
    color: #000000;
    font-size: 1.325em;
}
.exclusives h2 {
    border-color: #ffffff;
}
.highlights {
    background: #101010;
}
.highlights h2 span {
    background: #101010;
    color: #cccccc;
    position: absolute;
}
.highlights h2 {
    border-color: #464646;
}
.body p,
.body ul,
.body ol,
.body table {
    color: #ccc;
}
.single-meta a {
    color: #777;
}
.meta a,
.sticky li.first a:hover,
.featured-1 h2:hover,
.featured-2 h2:hover,
.text h2:hover,
.sticky li a:hover {
    color: #eb0254;
}
.meta a:hover {
    color: #ffffff;
}
.meta-author,
.meta-left {
    border-color: #404040;
}
.meta-share-fb,
.meta-share-tw,
.meta-share-rd {
    visibility: hidden;
}
.meta-share {
    width: 70px;
    float: right;
    height: 52px;
    padding-top: 7px;
    text-align: right;
}
.meta-left {
    width: calc(100% - 75px);
}
.body h1,
.body h2,
.body h3,
.body h4 {
    color: #9f0035;
}
.wp-caption-text {
    color: #bbb !important;
    background: #222222;
}
.wp-gallery a:first-child {
    max-width: 840px;
}
.wp-gallery.clearfix {
    min-width: 840px;
    margin-left: -30px;
}
.wp-caption.clearfix {
    max-width: 840px;
}
.wp-gallery.clearfix > a {
    border-color: #353535;
}
table {
    color: #ccc!important;
}
.tablepress .even td {
    background-color: #151515 !important;
}
.tablepress .odd td {
    background-color: #202020 !important;
}
.tablepress .even:hover td,
.tablepress .odd:hover td {
    background-color: #0d1e2e !important;
}
th  {
    background-color: #9f0035 !important;
    border-width: 0px !important;
}
.tablepress tbody td,
.tablepress tfoot th,
.tablepress tfoot th:last-child,
.tablepress thead th:last-child,
.tablepress tbody tr td:last-child {
    border-color: #353535;
}
table tr:nth-child(even) td {
    background: #1c1c1c;
}
table tr:nth-child(even):hover td {
    background: #1c1c1c !important;
}
table tr:nth-child(odd):hover td {
    background: #191919 !important;
}
.tablepress tfoot th,
.tablepress thead th {
    background-color: #272727;
}
.tablepress .row-hover tr:hover td {
    background-color: #464646;
}
.body table td, .body table th {
    border: 1px #444 solid;
}
.thumbnails-a .trc_rbox_header,
.thumbnails-a .syndicatedItem .video-title {
    color: #999999;
}
.lg-backdrop {
    background-color: #101010;
}
.lg-toolbar {
    background-color: #222222;
}
.lg-sub-html {
    background-color: #222222;
    color: #999999;
}
#lg-counter > div {
    filter: invert(0.95);
}
body > aside.sticky.wrap > div > section.sticky-hardware > h2 {
    border-color: #ff543a;
}
body > aside.sticky.wrap > div > section.sticky-gaming > h2 {
    border-color: #45d87c;
}
body > aside.sticky.wrap > div > section.sticky-mobile > h2 {
    border-color: #0fa6f3;
}
.sticky li.first a {
    min-width: 340px;
}
.section-head span {
    background: #464646;
    color: #ffffff;
}
.section-head {
    border-color: #464646;
}
.review-verdict {
    border-color: #fed432;
    background: #101010;
}
.review-verdict h3 {
    color: #45d87c!important;
    border-bottom-color: #444444;
}
.review-cons h3 {
    color: #EB0254!important;
}
img {
    filter: brightness(0.95);
}
blockquote {
    border-color: #464646;
}
.body blockquote{
    border-color: #666666;
}
#jp-relatedposts h3.jp-relatedposts-headline {
    margin: 0px 270px 0.5em!important;
    font-size: 2em!important;
}
.size-large,
.size-full {
    min-width: 100%;
}
main {
    background-color: #191919;
}
.featured .text {
	background: #111;
}
.sub-head {
	border-bottom: 1px solid #333;
}
.sub-head span {
	background: #333;
}
.featured-posts {
    background-color: #111;
}
.trending-posts li {
	border-bottom: 1px solid #333;
}
.post,
.youtube-channel {
    border-color: #111;
}
/*
messes up page with video
.post .text {
    padding-top: 13px;
    padding-left: 250px;
    padding-right: 10px;
}*/
.lSSlideOuter {
    background-color: #252525;
}
.review-info h3 {
    border-color: #333333;
    background: #333333;
}
.review-info,
.review-info div,
.review-info .right {
    border-color: #333333;
}
.feedback-button {
    background: #171717;
}
.popular-posts li a {
    color: #ddd;
}
p.wp-caption-text {
    width: 680px;
}
.wccf-gallery {
    background: #252525;
}
blockquote, .body .wccf-emphasis-quote {
    background: #232323;
    color: #5c5c5c;
}
blockquote p span {
    color: #ccc !important;
}
.single .sidebar section {
    margin-bottom: 50px;
}
body > main > section > div.body.clearfix > div > a {
    color: #aaaaaa!important;
}
p.wccf_related_post::before{
    color: #eb0454;
}
.wccf-stock-table tr:nth-child(odd) {
    background-color: #202020;
}
.wccf_related_post a {
    color: #acacac !important;
}
p.wccf_related_post {
	background-color: #292929;
}
p.wccf_related_post a.wccf_related_post::before {
	color: #EB0254;
}
.wccf_related_post a:hover,
.wccf_related_post a:active {
    background: #3c3c3c;
}
.wccf-products h4 span {
	background: #333;
    color: #bbb;
}
.mpp-toc {
    background: #222;
}
.wccf-products a {
    color: #999;
    box-shadow: 2px 3px 0 #444;
}
.accordian input:checked + label {
    color: #aaa;
    border: 1px solid #ccc;
    border-top: 2px solid #EB0254;
    border-bottom: none;
}
.accordian label {
    color: #ccc;
}
.accordian section {
    border-top: 1px solid #ccc;
}
.aside-event .more, .aside-deals .more, .navigation a {
    border: 2px solid #333;
}
.aside-event .more:hover, .aside-deals .more:hover, .navigation a:hover {
    border: 2px solid #333;
    background-color: #c40246;
    transition: all 0.25s ease;
}
footer a {
    color: #EB0254;
}
.dealsaccordian input:checked + label {
    color: #bbb;
    border-bottom: 1px solid #222;
}
.dealsaccordian label {
    color: #666;
}
.aside-finance h2 {
    margin: -40px -50px 40px -50px;
}
footer section {
    background-color: #222;
}
#adhesive_container {
    display: none !important;
}
}
