style.css:
@-moz-document domain("planfix.ru"), domain("planfix.com"), domain("planfix.ua") {
/*
ПланФикс - Тонированный Планировщик (by Kirr)
2018-11-14
*/


.planner-container .planner-block-list .planner-block-name {
    margin-right: -17px;
}

/* 
Light background-color: #d6d6d6;
Dark background-color: #c2cfc4;
*/

.planner-block-list-wrapper .planner-item.planner-item-list {
    background-color: #FFFFFF;
}

.planner-block-list-wrapper .planner-list-empty {
    background: none;
}

.planner-item-list .dlt-task-info {
    background-color: #FFFFFF;
}

.planner-block-list-h,
.planner-block-calendar-h-top,
.planner-page-new-block,
.planner-block-calendar .dl-fake-tbl-calendar-week .fake-tbl .fake-tbl-col.fake-tbl-col-hour,
.planner-block-calendar .dl-fake-tbl-calendar-week .fake-tbl .fake-tbl-col.fake-tbl-col-minute {
    background-color: #F0F0F0;
    font-size: 15px;
}

.planner-block-h-top.planner-block-list-h {
    margin-top: 10px;
    margin-left: 10px;
    margin-right: 10px;
    border: none;
}

.planner-block-calendar .planner-block-calendar-h-hour div,
.planner-block-calendar .fake-tbl .fake-tbl-col.fake-tbl-col-hour div {
    color: #8f8f8f;
    padding-top: 1px;
    padding-left: 4px;
}

.planner-block-calendar .dl-fake-tbl-calendar-week .fake-tbl .fake-tbl-col.fake-tbl-col-hour,
.planner-block-calendar .dl-fake-tbl-calendar-week .fake-tbl .fake-tbl-col.fake-tbl-col-minute {
    border-right: solid 1px #d5d5d5;
}

.planner-block-calendar-wrapper .dl-fake-tbl-h {
    background-color: #f7f7f7;
}

.planner-block-cards .planner-block-progress {
    margin-left: 10px;
    margin-right: 10px;
}

.planner-block-list-container {
    margin-right: 0px;
}

.planner-container > .wr-planner-vertical-blocks > .planner-blocks-vertical-list {
    margin-bottom: 6px; /* For lover edge not to stick onto screen */
}

.planner-project .planner-page-container .planner-container > .wr-planner-vertical-blocks > .planner-blocks-vertical-list {
    margin-bottom: 11px; /* For lover edge not to stick onto screen */
}

.planner-block {
    margin-top: 12px !important;
}

.chronicle  .planner-block-wr {
    margin-bottom: 3px;
}

.planner-block-items-container .baron_scroller {
    height: calc(100% - 20px) !important;
}

.planner-block-items-container.has-bottom-shadow:after {
    box-shadow: 0px 10px 10px 20px #d6d6d6;
    /*[[tintcolorgreenskip]]*/box-shadow: 0px 10px 10px 20px #c2cfc4;
    top: -14px;
}

/*
.planner-block-list-h .planner-block-list-h-tail {
    margin-top: -3px !important;
}
 2018-11-11 */

.planner-block-list-name span {
    position: relative;
    top: 1px;
}

.planner-block-cards .planner-block-progress {
    background-color: #d6d6d6;
    /*[[tintcolorgreenskip]]*/background-color: #c2cfc4;
}

.planner-block-cards .planner-list-quick-add {
    margin: 2px 0px 10px 0px;
    border: none;
}

.planner-list-quick-add.planner-list-quick-add-nogroup {
    padding: 3px 0px 3px;
}

.planner-container .planner-block-calendar,
.planner-container .planner-block-list {
    background-color: #d6d6d6;
    /*[[tintcolorgreenskip]]*/background-color: #c2cfc4;
    overflow: hidden;
    box-shadow: 2px 2px 3px 0px rgba(0,0,0,0.3);
}

.planner-block-list-container .planner-block-list-wrapper {
    /*padding-bottom: 2px;*/ /*leads to pointless full vertical scrollbar*/
    padding-left: 10px;
    padding-right: 10px;

    background-color: #d6d6d6;
    /*[[tintcolorgreenskip]]*/background-color: #c2cfc4;
}

.planner-blocks-vertical-list .planner-block:nth-child(n+2) {
    padding-top: 0px;
    margin-top: 4px;
}

.dl-task-microcard-container {
    margin-left: 0px;
    margin-right: 0px;
    /* border: none !important; 22.03.2018 */ 
    box-shadow: 0 1px 0 0 hsla(129, 12%, 61%, 1) !important;
}

.b-planner-block .planner-block-list-container .baron_scroller__bar {
    background: rgba(229, 229, 229, 0.9) !important;
}

.planner-block-list-container .baron_scroller__bar {
    display: block !important;
    width: 4px !important;
    border-radius: 2px !important;
    margin-right: 3px !important;
    border: none !important;
}

.planner-block-list .planner-block-list-container .baron_scroller:hover > .baron_scroller__bar {
    background: #f3f3f3 !important;
    width: 8px !important;
    border-radius: 4px !important;
    margin-right: 1px !important;
    border: none !important;
}

.planner-block-list .planner-block-chronicle-wrapper + .baron_scroller__bar {
    display: block;
    width: 4px;
    border-radius: 2px;
    margin-right: 3px;
    border: none;

    background: #ededed;
    background: #e5e5e5;
}

.baron_scroller:hover > .planner-block-chronicle-wrapper + .baron_scroller__bar {
    background: #f3f3f3;
    width: 8px;
    border-radius: 4px;
    margin-right: 1px;
    border: none;
}

.planner-block-calendar .baron_scroller:hover > .baron_scroller__bar {
    background: #f3f3f3;
    width: 8px;
    border-radius: 4px;
    margin-right: 2px;
    border: 1px solid #d0d0d0;
}

.planner-block-resizer,
.planner-page-new-block {
    margin-top: 10px;
}

.planner-block-resizer {
    margin-left: 3px;
    margin-right: 2px;
    width: 13px;
}

.wr-planner-vertical-blocks:nth-last-child(2) .planner-block-resizer:active {
    margin-left: 250px;
}


/*
.planner-project-main.planner-container {
    /* padding-left: 10px;
}   WHY??? FOR WHAT??? 
*/

.planner-project-main .planner-block-list-container > .baron_wrapper_scroll_redirect {
    /* tricky trick to hide dynamically created top border  */
    top: -1px;
}

.planner-container .planner-block-calendar:not(.collapsed-ver) {
    padding-left: 10px;
    padding-top: 10px;
    padding-right: 10px;
}

.planner-block-calendar .planner-block-tasks-resizer {
    border-bottom: none;
}

.planner-block-calendar .planner-block-calendar-h {
    overflow: hidden;
}

.planner-block-calendar .planner-block-calendar-h .calendar-date-h:last-child {
    border-right: none;
    padding-right: 1px;
}

.planner-block-calendar .dl-fake-tbl-calendar-week .fake-tbl .fake-tbl-col:last-child {
    border-right: none;
    padding-right: 2px;
}

.planner-block-tasks-container.jspScrollable .jspDrag {
    border-radius: 4px;
    left: -5px;
    width: 8px;
}

.planner-page-container > .planner-page-new-block {
    background: none;
    border: none;
    color: #808080;
}

.planner-page-container > .planner-page-new-block > .planner-page-new-block-header {
    font-weight: normal;
}

.planner-page-new-block-item {
    border: none;
    font-size: 13px;
}

.planner-page-new-block-item:hover {
    background-color: #FFFFFF !important;
}

.dl-task-microcard-container:not(.task-card-opened) {
    border: 2px solid rgba(120, 120, 120, 0) !important;
    background-color: #FFFFFF;
}

.dl-task-microcard-container:not(.task-card-opened):hover {
    border: 2px solid rgba(120, 120, 120, 0) !important;
    background-color: #f7f7f7;
}

.dl-task-microcard-container.task-card-opened {
    /* border: 2px dotted rgba(120, 120, 120, 0.75) !important; 22.03.2018 */
    border-width: 2px !important;  /* 22.03.2018 */
    background-color: #FFFFFF;
}

.dl-task-microcard-container.task-card-opened:hover {
    /* border: 2px dotted rgba(120, 120, 120, 1) !important; 22.03.2018 */
    border-width: 2px !important;  /* 22.03.2018 */
    background-color: #f7f7f7;
}

.planner-block .chronicle-item-container {
    background-color: white;
}

.planner-block-chronicle-container .chronicle-wrapper {
    margin-left: 10px;
    margin-right: 10px;
    margin-bottom: 16px;
}

.planner-block.chronicle-item-container-small .chronicle-item-login {
    padding-left: 10px;
}

.planner-block-chronicle-wrapper .chronicle-date {
    color: #7a7a7a;
    border-top: none;
}

.chronicle-item-container > tbody > tr > td {
    border-top-color: #d6d6d6;
    /*[[tintcolorgreenskip]]*/border-top-color: #c2cfc4;
}  /* 22.03.2018 */

/* 22.03.2018
.planner-block-chronicle-wrapper .chronicle-item-container.chronicle-item-container-active,
.planner-block-chronicle-wrapper .chronicle-wrapper .tbl-list .summary-item-active td {
    border: 2px dotted rgba(120, 120, 120, 0.75);
}
*/

/* 22.03.2018
.planner-block-chronicle-wrapper .chronicle-item-container.chronicle-item-container-active:hover,
.planner-block-chronicle-wrapper .chronicle-wrapper .tbl-list .summary-item-active td:hover {
    border: 2px dotted rgba(120, 120, 120, 1);
}
*/


.planner-block-chronicle-wrapper .empty-message {
    border: none;
}

.planner-block-chronicle-wrapper .empty-message div {
    margin-top: 20px !important;
}
.planner-block-chronicle-wrapper .empty-message img {
    width: 51px;
    height: 49.5px;
    margin-bottom: 10px !important;
}

.planner-block-chronicle-wrapper .empty-message span {
   font-size: 15px;
}

.planner-block-chronicle-wrapper .empty-message span.empty-strong a {
   font-size: 15px;
}


.planner-block-fadeout-bottom {
    box-shadow: 0px 0 15px 10px #d6d6d6 !important;
    /*[[tintcolorgreenskip]]*/box-shadow: 0px 0 15px 10px #c2cfc4 !important;
}
  
.planner-blocks-vertical-list .planner-block.planner-block-calendar:nth-child(n+2) {
    padding-top: 11px;
}

  
.planner-block-calendar .planner-schedule-container .planner-schedule-header {
    background-color: #f0f0f0;
}

.planner-block-calendar .planner-schedule-container .planner-schedule-header .header-r .scroller-b .scroll {
    background: #fbfbfb;
    border-radius: 4px;
    border: 1px solid #d0d0d0;
}

.planner-block-calendar .planner-schedule-container .planner-schedule-header .header-r .hours-b .hour {
    color: #8f8f8f;
}

.planner-block-calendar .planner-schedule-container .fake-tbl-inl .fake-tbl-inl-row {
    background: white;
}

/* New corrections */

.planner-block-calendar:not(.planner-calendar-block-with-left-time) .planner-block-calendar-container .baron_wrapper.baron_wrapper_scroll_redirect {
    margin-bottom: 20px;
}

.planner-blocks-vertical-list.time-left-pad .planner-block-calendar:not(.planner-calendar-block-with-left-time) .planner-block-calendar-container .baron_wrapper.baron_wrapper_scroll_redirect .baron_scroller {
    height: calc(100% - 0px) !important;
}

.planner-blocks-vertical-list:not(.time-left-pad) .planner-block-calendar:not(.planner-calendar-block-with-left-time) .planner-block-calendar-container .baron_wrapper.baron_wrapper_scroll_redirect .baron_scroller {
    height: calc(100% - 10px) !important;
}
 
.planner-blocks-vertical-list:not(.time-left-pad) .planner-block-calendar .planner-block-tasks-container:not(.planner-block-force-hidden) ~ .planner-block-calendar-container .baron_wrapper.baron_wrapper_scroll_redirect {
    margin-bottom: 10px;
}



.planner-calendar-block-with-left-time .planner-block-tasks-container:not(.planner-block-force-hidden) ~ .planner-block-calendar-container .baron_wrapper.baron_wrapper_scroll_redirect .baron_scroller {
    height: calc(100% - 20px) !important;
}

.planner-calendar-block-with-left-time .planner-block-tasks-container.planner-block-force-hidden ~ .planner-block-calendar-container .baron_wrapper.baron_wrapper_scroll_redirect {
    margin-bottom: 9px;
}

.planner-calendar-block-with-left-time .planner-block-tasks-container.planner-block-force-hidden ~ .planner-block-calendar-container .baron_wrapper.baron_wrapper_scroll_redirect .baron_scroller {
    height: calc(100% - 12px) !important;
}

.planner-block.collapsed-ver {
    width: calc(100% - 1px) !important;
}

.planner-item-list {
    border-bottom: 1px solid #d6d6d6;
    /*[[tintcolorgreenskip]]*/border-bottom: 1px solid #c2cfc4;
}  /* 22.03.2018 */


 
.planner-block .planner-block-total-header {
    height: 43px;
}

.planner-block .planner-block-total-header .planner-block-total-value {
    bottom: 48px;
}    

.planner-block .planner-block-total-header {
    padding-left: 10px;
    padding-right: 10px;
    border-top: 1px solid #d6d6d6;
    /*[[tintcolorgreenskip]]*/border-top: 1px solid #c2cfc4;
}



/* For kanban grid */
.planner-block-kanban-grid .planner-kanban-fields-header .planner-kanban-field {
    width: 100%;
    margin-right: 15px;
    text-align: center;
    vertical-align: middle;
    color: #353535;
    font-size: 13px;
}

.planner-kanban-fields-header{
    width: calc(100% - 20px);
    padding-left: 10px;    
}

.planner-kanban-tasks.baron_container {
    width: calc(100% - 20px);
    padding-left: 10px;
}

.planner-block-kanban-grid .baron_scroller__bar {
    background: rgba(229, 229, 229, 0.9) !important;
}

.planner-block-kanban-grid .baron_scroller__bar {
    display: block !important;
    width: 4px !important;
    border-radius: 2px !important;
    margin-right: 3px !important;
    border: none !important;
    right: 0px;
}

.planner-block-kanban-grid .baron_scroller:hover > .baron_scroller__bar {
    background: #f3f3f3 !important;
    width: 8px !important;
    border-radius: 4px !important;
    margin-right: 1px !important;
    border: none !important;
}

.planner-block-kanban-grid::after {
    content: '';
    position: relative;
    display: block;
    top: -17px;
    box-shadow: 0px 0 15px 10px #d6d6d6 !important;
    /*[[tintcolorgreenskip]]*/box-shadow: 0px 0 15px 10px #c2cfc4 !important;
    box-shadow: 0px 10px 10px 20px #d6d6d6;
    /*[[tintcolorgreenskip]]*/box-shadow: 0px 10px 10px 20px #c2cfc4;
}

.planner-kanban-tasks-wrapper .baron_wrapper {
    border-top: none !important;
}

.planner-kanban-tasks.baron_container {
    padding-bottom: 8px;
}

.planner-block-kanban-grid .planner-kanban-tasks-group-header {
    height: 35px;
    color: #737373;
    background-color: #eae7e7;
}

.planner-block-kanban-grid .planner-kanban-tasks-group-header.floating {
    margin-left: 10px;
}



/* небольшой фикс для нижней полосы прокрутки */
.leftmenu-container+.page-layout-block .page-layout-block-resizer, .leftmenu-container+.page-layout-block .page-layout-resize-overlay::before, .cardmenu.handbook-filter-list+.page-layout-block .page-layout-block-resizer, .cardmenu.handbook-filter-list+.page-layout-block .page-layout-resize-overlay::before {
    height: 100%;
}

.planner-project-main.planner-container::-webkit-scrollbar,
.b-main-block::-webkit-scrollbar {
    height: 16px !important;
}

.b-main-block::-webkit-scrollbar-track
{
	margin-left: 8px !important;
}

.planner-project-main.planner-container::-webkit-scrollbar-thumb,
.b-main-block::-webkit-scrollbar-thumb {
    border: 1px solid #d0d0d0 !important;
    background-color: #e7e7e7 !important;
}


.planner-block-collapse-b-ver .planner-block-collapse-count {
    top: 35px;
    font-size: 18px;
    font-weight: normal;
}

.planner-block-collapse-b-hor .planner-block-collapse-count {
    font-size: 18px !important;
    font-weight: normal;
}


}
