style.css:
body {
    background-image: none;
    background-color: #292929;
}
h1 {
    color: #c10a27 !important;
}
footer {
    color: white;
}
.box {
    background-color: white;
}
info {
    color: white;
    background-color: firebrick;
}
info>i {
    color: yellow;
}
#BasePageContainer::after,
content::after {
    float: left;
    color: #c10a27;
    font-size: 11px;
    content: "Styled by: Ezra Rieben";
}
header span.hLink:not(:last-child)::after {
    color: firebrick;
}
#mainGrid {
    background-color: transparent;
}
ul.minimizedBoxes li {
    background-color: #fff;
}
#loadingIndicator {
    color: firebrick;
}
navigation {
    display: none;
}
#mainGrid {
    box-shadow: 1px 2px 17px 4px firebrick;
    -moz-box-shadow: 1px 2px 17px 4px firebrick;
    -webkit-box-shadow: 1px 2px 17px 4px firebrick;
}
.bildungsangebot .unit {
    background-color: #c10a27 !important;
    color: white !important;
}
.bildungsangebot .unit span.title {
    color: white !important;
}
.bildungsangebot .unit span {
    color: lightgrey;
}
.bildungsangebot .unit span i {
    color: yellow;
}
.icon-home,
.icon-print {
    font-size: 30px !important;
}
