style.css:
@-moz-document domain("thecurrent.org") {
html {
    background: #000000;
}
.listen-latest {
    border: 1px solid #4a4a4a;
    padding: 10px;
    background: #000000;
}
.listen-header {
    background: #000000;
}
.media,
.media:visited {
    color: #ffffff;
}
.listen-switch-label {
    color: #ffffff;
}
.listen-give-button,
.listen-give-button:visited {
    color: #ffffff;
    border: 2px solid #ffffff;
}
.listen-switch-label svg {
    fill: #ffffff;
}
}
