style.css:
@-moz-document domain("lds.org"){
.narrow #secondary,
#secondary {
    width: 29.5%;
}
.narrow .two-col #primary {
    padding-left: 15px;
    padding-right: 15px;
}
.narrow #platform-canvas-nav {
    width: 15%;
}
.narrow #platform-canvas-content {
    max-width: 85%;
}
.wrapper {
    max-width: 100%;
    width: 90%;
}
.pf-responsive #platform-canvas {
    max-width: 100%;
    width: 90%;
}
h3 {
    font: 20px/22px "Helam Slab", "Roboto Slab", Calibri, Helvetica, Arial, sans-serif;
}
}
