	background: #171B1F;
	}
  
#mainBarFX{
  background-color: #272B35;
  }
  
#mainBar{
  background-color: #171B1F;
  width:auto!important;
  max-width: 1000px !important;
  margin:0 auto !important;
  padding:0 20px !important;
}
/*Cuadro de busqueda/search box*/
  
#herculeFrame{
    background-color: #272b35;
  }
  h1#feedlyTitleBar{
    color: #BAD3DB;
