@-moz-document domain("www.interpals.net") {
/*** Init ***/

/* Force CSS3 border-box */

*,
*:before,
*:after {
    box-sizing: border-box;
}

/* Force display of items */

#logoAdBar,
#gradBgBox {
    display: block !important;
}

/* Strip page of ads, this will not impact load time as ads will still be loaded, please use an ad blocker to solve this issue */

[id^="ad"],
[id*="Ad"],
[class^="ad"],
[class*="Ad"] {
    display: none;
}

/* Fix Browser Warning */

#browserWarning {
    position: fixed;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    z-index: 9999;
}

/* Remove unnecessary space(s) */

#logoAdBar {
    height: 0px;
}

.topLogo + .topLB {
    display: none;
}

/* Extend width of container */

#page, #mc_box {
    width: 100%;
    
}

#page {
    max-width: 100%;
    margin: 0 !important;
}

#mc_box {
    width: 100%;
    max-width: 1280px;
    margin: 0 auto;
}

/*** Main Page ***/

/* Remove side scroll */

body {
    overflow-x: hidden;
}

/* Remove border-radius of main page */

#mc_box > * {
    border-radius: 0px !important;
}

/*** Nav Bars ***/

/* Top bars */

.topMemberNav,
#topNav {
    position: fixed;
    left: 0;
    height: 32px;
    width: 100%;
    overflow: visible;
    z-index: 999;
}

/* Nav: Class{topMemberNav} */

.topMemberNav {
    top: 0;
}

.topMemberNav .cCont,
.topMemberNav .cOut,
.topMemberNav .cIn,
.topMemberNav .memberLinks,
.topMemberNav .memberLink {
    height: 100%;
}

.topMemberNav a.memberLink {
    padding: 6px 10px;
    border-color: transparent;
    font-size: 14px;
    font-weight: normal;
    text-align: center;
    transition: background .2s ease-in-out,
                color .2s ease-in-out;
}

.topMemberNav a.memberLink:first-child {
    display: none;
}

.topMemberNav #pmNewCnt {
    display: inline-block;
    height: 16px;
    width: 32px;
    margin-bottom: -3px;
    border-radius: 25px;
    font-size: 11px;
    text-align: center;
    overflow: hidden;
    transition: background .2s ease-in-out,
                color .2s ease-in-out;
}

.topMemberNav #pmNewCnt:empty {
    width: 0;
}

.topMemberNav #pmNewCnt:empty:after {
    content: "0";
}

/* Nav: id{#topNav} */

#topNav {
    top: 32px;
    box-shadow: 0px 5px 8px 0px rgba(0,0,0,.25);
    transition: background .2s ease-in-out,
            	border-color .2s ease-in-out;
}

#topNav > *,
#topNav > * > *,
#topNav > * > * > a {
    height: 100%;
}

#topNav > * > * > a {
    padding-top: 6px;
    padding-bottom: 6px;
    border-color: transparent;
    text-transform: capitalize;
    transition: background .2s ease-in-out,
                color .2s ease-in-out;
}

#topNav > * > * > .bb {
    display: none;
}

#topNav [href="/"] {
    position: fixed;
    top: 0;
    left: 0;
    height: 64px;
    padding-top: 22px;
    padding-bottom: 22px;
	border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: transparent;    
    border-right-width: 1px;
    border-right-style: solid;
    border-right-color: transparent;
    font-size: 10px;
    font-weight: bold;
    text-transform: capitalize;
    line-height: 20px;
    overflow: visible;
    transition: border-top-color .2s ease-in-out,
                border-left-color .2s ease-in-out,
                border-bottom-color .2s ease-in-out,
                border-right-color .2s ease-in-out,
    			background .2s ease-in-out,
    			color .2s ease-in-out;
}

/* Topbar Icons */

.topMemberNav .yb_cont,
#topNav .bb {
    display: inline-block;
    width: 22px;
    background: none;
    margin-right: 4px;
    font-family: FontAwesome;
    font-style: normal;
    font-weight: normal;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 18px;
}

#yb2:before {
    content: "\f007";
}

#yb3:before {
    content: "\f0e0";
}

#yb4:before {
    content: "\f0c0";
}

#yb5:before {
    content: "\f09e";
}

#yb6:before {
    content: "\f03e";
}

#yb7:before {
    content: "\f0f6";
}

#yb8:before {
    content: "\f02e";
}

#yb9:before {
    content: "\f0ad";
}

#bb1 {
    margin-right: 4px;
    font-size: 24px;
    overflow: visible;
}

#bb1:before {
    content: "\f041";
}

#bb2:before {
    content: "\f002";
}

#bb3:before {
    content: "\f022";
}

#bb4:before {
    content: "\f111";
}

#bb5:before {
    content: "\f0ec";
}

/* Nav: Class{yellowFooter} */

.yellowFooter {
    margin-left: -100%;
    margin-right: -100%;
    border-top-width: 1px;
    border-top-color: transparent;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: transparent;
}

/* Nav: Class{bottomNav} */

#mc_box .bottomNav {
    margin-left: -100%;
	margin-right: -100%;
}

.bottomNav .copy {
    position: relative;
    top: -10px;
    padding-top: 32px;
    padding-bottom: 32px;
}

.bottomNav .copy a {
    text-decoration: underline;
}

.bottomNav .copy span:last-child {
    width: 0;
}

.bottomNav .copy span:last-child:after {
    position: relative;
    left: -16px;
    -webkit-user-select: none;
    -moz-user-select: none;
    user-select: none;
	content: "";
}

/*** Main Content ***/

/* Push down main content to make room for new headers */

#gradBgBox,
.yellowFooter,
.bottomNav {
    position: relative;
    top: 96px;
}

#gradBgBox {
    margin-bottom: 32px;
    box-shadow: 0px 0px 4px 0px rgba(0, 0, 0, .25);
}

/** Main Container **/

#mainContainer {
    padding-top: 20px;
    padding-bottom: 20px;
}

/*** Profile ***/

/* Profile Main */

#profBody {
    padding-left: 20px;
    padding-right: 20px;
}

#profBody > * {
    width: 100%;
}
  
#profBody > .myProfNotice {
    display: none; /* You can enable this notice by replacing "none" with "inline-block or block" */
    margin-top: 0;
    padding: 5px 20px;
    border-radius: 0px;
    line-height: 3;
}

#profBodyTop {
    padding-top: 12px;
    padding-bottom: 12px;
}

#profPhotos .mpImgWrap {
    margin: 0;
}

#profPhotos a.profPhotoLink {
    margin: 4px 1px 0px 0px;
}

.profDataBox > * {
    width: 95% !important;
}

/** Profile Right Side Menu **/

/* Remove injected space */

#profRightBar > :first-child[style] {
    display: none;
}

/* Remove border-radius on map */

.profRightMap {
    border-radius: 0px;
}

/* Fix Alignment on Status */

#prStatMsgIcon {
    margin: 6px 4px 0 0;
}

#prStatMsgBody {
    line-height: 1;
}

#asEditLink {
    padding: 6px;
    line-height: 1;
}
}

@-moz-document regexp("(https?:\\/\\/)(w{3}?)\\.interpals.net\\/pm\\.php.*") {
/*** Messages ***/

#mainContainer {
    overflow: visible;
}

.pm_thread .th_wrap {
    height: 80px;
}

#narrowRightBar {
    display: none;
}

#mainContWBar {
    margin-top: -20px;
    margin-bottom: -46px;
    overflow: visible;
}

#controls {
    padding: 0px 14px;
}

#controls .mark_wrap {
    padding: 0px;
    width: 140px;
    border: none;
}

#fbu_wrap input[type="text"] {
    height: 18px;
    width: 100px;
    margin-left: 14px;
    margin-top: 7px;
    padding-right: 20px;
    line-height: 18px;
}

#find_contact {
    left: 96px;
    padding: 3px !important;
    font-size: 12px;
}

#threads_main {
    height: 406px !important;
}

#threads_main.full #threads-left,
#threads_main.full #threads_right {
    height: 100% !important;
}

#threads_left .tui_username {
    position: relative;
}

#threads_left .online-now,
#threads_left .offline-now {
    position: absolute;
    height: 14px;
    width: 14px;
    margin-right: 0;
    border-radius: 100% 0 0 100%;
    line-height: 14px;
    z-index: 1;
}

#threads_left .online-now:before {
    content: "\f058";
}

#threads_left .offline-now:before {
    content: "\f057";
}

#threads_left .online-now + .tui_el,
#threads_left .offline-now + .tui_el {
    height: 14px;
    width: 100%;
    padding-left: 20px;
    border-radius: 42px;
    line-height: 12px;
}

#threads_main.full #threads_right {
	height: 360px !important; /* Fallback incase of non-calc support */
	height: calc(100% - 46px) !important;
    padding-left: 32px;
    padding-right: 32px;
}

#threads_main.full.send #threads_right {
	height: 220px !important; /* Fallback incase of non-calc support */
	height: calc(100% - 186px ) !important;
}

#threads_right .pm_msg,
#threads_right .msg_last_online {
    width: 100%;
    margin: 0;
}
}

@-moz-document domain("www.interpals.net") { /*[[theme]]*/ }
@-moz-document domain("www.interpals.net") { /*[[fa47]]*/ }
@-moz-document domain("www.interpals.net") { /*[[newflag]]*/ }

