style.css:
@-moz-document url-prefix(resource://activity-streams/data/content/activity-streams.html) {
	body {
		color: #CCC;
		background-color: #161616;
	}

	form.search-wrapper input {
		background-color: #292929;
		border-color: #292929;
	}

	form.search-wrapper button {
		background-color: #393939;
		border-color: #393939;
	}

	form.search-wrapper input::-webkit-input-placeholder  {
		color: #C1C1C1 !important;
	}
	form.search-wrapper input:-moz-placeholder  {
		color: #C1C1C1 !important;
	}
	form.search-wrapper input::-moz-placeholder  {
		color: #C1C1C1 !important;
	}
	form.search-wrapper input:-ms-input-placeholder {
		color: #C1C1C1 !important;
	}

	.spotlight-title {
		color: #F1F1F1;
	}

	.spotlight-description {
		color: #C1C1C1;
	}

	.spotlight-item,
	.site-icon,
	.site-icon-title,
	.site-icon-background,
	.content,
	.context-menu {
		background-color: #292929 !important;
	}

	.context-menu-link, .submenu {
		color: #C1C1C1 !important;
	}

/*[[Q1]]*/
}
