style.css:
@-moz-document domain("archiveofourown.org") {
/* Updated on 14 Feb  2021 */
body, #header {
	background: #1e1e1e !important;
	color: #cecece !important;
}

a, a:link, a:visited, a:visited:hover {
	text-decoration: underline !important;
	border-bottom: none !important;
}

#main, #outer, .wrapper {
	background-color: #1E1E1E !important;
	color: #CECECE !important;
}

ul.actions {
	padding: 0 0 0 0 !important;
}

.actions input, input[type="submit"] {
	color: #CECECE !important;
}

.blurb h4 a:visited {
	color: #970000;
}

.blurb h4 a:visited:hover {
	color: #933;
}

.blurb h4 a:link:hover, .expander {
	color: #999!important;
}

.timeline-Widget {
	background-color: #090909 !important;
}

.preface h3 {
	font-size: 100% !important;
}

.userstuff {
	font-size: 100% !important;
	font-family: "Lucida Sans Unicode", 'Lucida Sans Unicode', Verdana, sans-serif;
	text-align: justify;
}

.userstuff h2 {
	color: #ce1e1e !important;
}

.splash .intro {
	background: #1e1e1e !important;
	color: #cecece !important;
	box-shadow: none !important;
}

.splash #signup {
	text-shadow: none !important;
}

#workskin {
	margin: 0px!important;
	max-width: 100%!important;
}

#workskin .black, #workskin .pesterlog {
	color: #cecece;
}

#footer {
	background: #1e1e1e;
	border-top: 1px solid #3e3e3e !important;
}

#footer a, #footer .secondary {
	background: transparent !important;
	border: none !important;
}

#header .menu {
	background-image: none;
	background: #2e2e2e;
	box-shadow: none !important;
}

#header .primary .menu .current {
	color: #d7d7d7;
}

#header .primary {
	background: #970000 !important;
}

#header .dropdown:hover a {
	background: #2e2e2e !important;
	color: #cecece;
}

#header .actions a:hover {
	background: #3e3e3e !important;
}

#header fieldset {
	border: none !important;
}

#header .heading a {
	color: #cecece !important;
}

#header .user .current, #header .user {
	color: #cecece !important;
	background-color: #1e1e1e;
}

#header .open a {
	color: #cecece !important;
	background: #2e2e2e !important;
	background-image: none;
	box-shadow: none !important;
}

#header .actions a:focus {
	background: inherit;
	
}

#header a {
	background: none !important;
	border: none !important;
}

#small_login {
	color: #cecece !important;
	background: #1e1e1e !important;
	border: 1px solid #3e3e3e;
}

#small_login dt {
	border: none !important;
}

.dropdown #small_login .footnote a {
	background: #1e1e1e;
}

.dropdown #small_login .footnote a:hover {
	background: #2e2e2e !important;
}

.action, #small_login .action {
	color: #cecece !important;
	background: #2e2e2e;
	background-image: none;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
}

.action:hover, #small_login .action:hover {
	color: #cecece !important;
	background: #3e3e3e;
	background-image: none;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
}

.actions a {
	background: #2e2e2e !important;
	background-image: none !important;
	text-shadow: none;
	border: 1px solid #3e3e3e !important;
	padding: .25em .55em !important;
	text-decoration: none !important;
}

.actions a:hover {
	background: #3e3e3e !important;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
	color: #cecece;
}

.actions a:visited {
	color: #cecece !important;
	text-shadow: none !important;
}

.actions input, input[type="submit"] {
	background: #2e2e2e !important;
	background-image: none;
	box-shadow: inset 0 -8px 4px #232323, inset 0 8px 7px #555 !important;
	border: 1px solid #3e3e3e;
}

.actions input:hover, input[type="submit"]:hover {
	background: #3e3e3e !important;
	background-image: none;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
}

.actions label {
	color: #cecece !important;
	background: #2e2e2e !important;
	background-image: none;
	border: 1px solid #3e3e3e;
}

.actions label:hover {
	color: #cecece !important;
	background: #3e3e3e !important;
	background-image: none;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
}

.action:link {
	background: #2e2e2e;
}

.action:link:hover {
	background: #3e3e3e;
}

.wrapper {
	box-shadow: none;
	background-color: #1e1e1e;
}

#outer.wrapper, #inner.wrapper {
	background-color: #1e1e1e;
}

.heading {
	text-shadow: none;
}

a:link {
	color: #cecece !important;
}

a:visited:hover {
	color: #666;
}

a.tag, .tags a {
	color: #cecece !important;
	line-height: 1.6;
	text-decoration: none !important;
	border-bottom: 1px dotted !important;
}

fieldset {
	background: #1e1e1e;
	box-shadow: none;
	border: 1px solid #3e3e3e;
}

fieldset fieldset, fieldset dl dl, form blockquote.userstuff {
	background: #1e1e1e;
	box-shadow: none;
}

fieldset dl {
	border: none !important;
}

li.blurb {
	box-shadow: none;
	border: 1px solid #3e3e3e;
}

li.relationships a {
	background: #1e1e1e;
}

li.comment {
	border: 1px solid #3e3e3e !important;
}

.comment h4.byline {
	background: #3e3e3e !important;
}

div.comment {
	border: none !important;
}

.notice, .comment_notice, ul.notes, .caution, .error, .comment_error {
	color: #cecece !important;
	background-color: #2e2e2e !important;
	box-shadow: none !important;
	border: 1px solid #3e3e3e !important;
}

.comment div.icon {
	border-right: 1px solid #3e3e3e;
	border-bottom: 1px solid #3e3e3e;
}

.listbox {
	background: #1e1e1e !important;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
}

.listbox .index {
	background: #1e1e1e !important;
	box-shadow: none !important;
}

.listbox .heading, .listbox .heading a:visited {
	color: inherit;
}

#dashboard.own {
	border-top: none;
	border-bottom: none;
	box-shadow: none !important;
}

#dashboard ul {
	border-top: 2px solid #3e3e3e;
}

ul.work.navigation.actions li a, ul.navigation.actions li a, ul.actions li a, ol.pagination.actions li a {
	box-shadow: inset 0 -8px 4px #232323, inset 0 8px 7px #555;
}

#feedback > ul li a {
	box-shadow: inset 0 -8px 4px #232323, inset 0 8px 7px #555 !important;
}

dl.index dd {
	background: #1e1e1e;
}

textarea {
	color: #cecece !important;
	background-color: #262626!important;
	box-shadow: none !important;
	border: 1px solid #3e3e3e !important;
}

select, input {
	color: #cecece !important;
	background: #262626!important;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
}

.secondary {
	color: #cecece !important;
	background-color: #1e1e1e !important;
	box-shadow: none !important;
	border: 2px solid #3e3e3e;
}

.verbose legend, form.verbose legend {
	background: #1e1e1e;
	border: 1px solid #3e3e3e !important;
	box-shadow: none !important;
	border-top: solid 2px #3e3e3e !important;
}

.verbose form legend {
	background: #1e1e1e;
}

label {
	color: #cecece !important;
}

.toggled form, .dynamic form  {
	color: #cecece !important;
	background: #1e1e1e !important;
	box-shadow: none !important;
	border: 1px solid #3e3e3e !important;
}

form dl {
	background-color: #1e1e1e!important;
	box-shadow: none !important;
	border: 1px solid #3e3e3e;
}

form dt {
	border-bottom: 1px solid #3e3e3e !important;
}

#modal {
	background: #1e1e1e !important;
	color: #cecece !important; 
	border: 10px solid #2e2e2e !important;
}

#modal .content {
	border-bottom: none !important;
}

#modal a:link {
	border-bottom: 1px solid #3e3e3e;
}

#modal .footer .action.modal-closer {
	border-color: #3e3e3e;
}

#symbols-key dl {
	border: 1px solid #3e3e3e;
}

.thread .even {
	background: #262626 !important;
}

span.question {
	background: #2e2e2e;
	border-color: #cecece;
}

span.symbol {
	color: #cecece;
}

.splash {
	max-width: 98% !important;
}

.splash .module div.account {
	background-color: #1e1e1e;
	border: 1px solid #3e3e3e;
}

.splash .favorite li:nth-of-type(odd) a {
	background: #2e2e2e;
}

.splash .browse li a:hover {
	color:#999999 !important;
}

.tweets iframe {
	width: 78em !important;
	height: 78em !important;
}

.timeline-Tweet-text {
	font-size: 14px !important;
}

.bookmark .user {
	background: #1e1e1e;
	border: 1px solid #3e3e3e;
}

.bookmark .count a {
	color: #069 !important;
}

dl.meta {
	background: #1e1e1e;
	border: 1px solid #3e3e3e;
}

.splash .module h3, .splash .news li {
	border-bottom: 1px solid #3e3e3e;
}

.splash .module h3 {
	color: #aa1818;
}

.home .header h2 {
	border-bottom: none;
}

.search input[type="text"] {
	background: #262626 !important;
	border-radius: 0.25em;
	border: 1px solid #3e3e3e;
	box-shadow: none!important;
}

.preface h3 {
	border-bottom: 1px solid #3e3e3e;
	font-size: 125%!important;
}

.chapter .preface {
	border-top: 1px solid #3e3e3e;
}

.required .autocomplete, .autocomplete .notice {
	color: #cecece;
}

.draft {
	background: #1e1e1e;
	border: 2px dashed #3e3e3e;
}

.picture .header {
	border-bottom: 1px solid #3e3e3e;
}

fieldset fieldset.listbox {
	border: 1px solid #3e3e3e;
}

#advanced_skin_fieldset a {
	border: 1px solid #3e3e3e;
}

.reading h4.viewed {
	background: transparent;
}

