	border-color: #3e3e3e !important;
}

th {
	background: #242424;
	border: 1px solid #3e3e3e;
}

thead {
	border-bottom: 2px solid #3e3e3e;
}

tbody {
	background: #1e1e1e;
}

tr {
	border: 1px solid #3e3e3e !important;
}

tr:hover {
	background: #2e2e2e;
}

p.character_counter {
	color: #cecece;
}

.dropdown {
	background: none;
	color: #cecece;
}

.autocomplete div.dropdown ul {
	color: #cecece;
	background: #1e1e1e;
	border: 1px solid #3e3e3e;
}

.autocomplete .dropdown ul li:hover, li.selected {
	color: #cecece;
	background: #970000;
}

a.modal.help {
	border: none !important;
	text-decoration: none !important;
}

.help.symbol.question.modal.modal-attached {
	background: none !important;
}

ul.notes li, .error ul li {
	background: #2e2e2e;
}

.footnote {
	color: #cecece;
}

rect {
	fill: #1e1e1e;
}

text[fill="#000000"] {
	fill: #cecece;
}

text[fill="#222222"] {
	fill: #cecece;
}

text[fill="#444444"] {
	fill: #cecece;
}

rect[fill="#3366cc"] {
	fill: #3366cc;
}

rect[fill="#cccccc"] {
	fill: #3e3e3e;
}

rect[fill="#333333"] {
	fill: #1e1e1e;
}

rect[fill="none"] {
	fill: none;
}

rect[stroke="#000000"] {
	stroke: #fff;
}

path[fill="#ffffff"] {
	fill: #2e2e2e;
}

path[stroke="#cccccc"] {
	stroke: #3e3e3e;
}

.unread, .unreviewed {
	background-color: #262626;
}

.actions a {
	color: #cecece;
}

#dashboard a {
	background: transparent !important;
	border: none !important;
}

#dashboard a:hover {
	background: #2e2e2e !important;
}

#dashboard .current, .actions a:active, #outer .current, a.current, .current a:visited, span.unread, .replied, span.claimed, dl.index dd, .own, .draft, .draft .unread, .child, .unwrangled, .unreviewed, .ui-sortable li:hover{
    color: #fff;
    background: #333;
    border-color: #555;
    box-shadow: -1px -1px 3px #000;
}

span.unread, .replied, span.claimed, .actions span.defaulted {
	background-color: #1e1e1e;
	border-color: #3e3e3e;
	color: #aa1818;
}

form dd.required {
	color: #cecece;
}

div[class="new dynamic"] {
	background-color: #1e1e1e;
}

.added.tag {
	color: #cecece;
}

.actions a:hover, .actions input:hover, .actions a:focus, .actions input:focus, label.action:hover, .action:hover, .action:focus {
	box-shadow: none;
}

