.stories_feed_item_ava,
.audio_feed_mem_photo,
.app_widget_image {
    border-radius: 0 !important;
}
/*друзья в группах*/

.group_friends_image {
    border-radius: 0;
    border: none;
    margin: 0 0 0 5px;
    height: 40px;
    width: 40px
}
.group_friends_text {
    line-height: 1.7
}
/*левое меню*/

.LeftMenu__icon,
.left_icon.fl_l,
.more_div,
.LeftNav__icon {
    display: none
}
.left_label.inl_bl {
    padding-left: 10px;
    background: #fff
}
.left_label.inl_bl:hover {
    background: #f0f2f5
}
.side_bar_inner > div.left_menu_nav_wrap:last-child {
    visibility: hidden!important;
}
.more_div.l_main {
    visibility: hidden!important;
}
.side_bar_inner {
    position: fixed !important;
    top: 0;
    margin-top: 43px !important;
    z-index: 99;
}
/*поиск*/

#ts_input {
    border-radius: 0
}
/*шапка*/

/*ровные блоки*/

.page_block,
._post {
    border-radius: 0!important
}

/*непрочитанные*/

.im-mess.im-mess_unread:last-child:before,
.im-mess.im-mess_unread+.im-mess:before {
    background: #d9d9d9;
}
/*Фон непроитанного сообщения*/

.im-mess.im-mess_unread {
    background: #d9d9d9 !important
}
/*напишите сообщение*/

.im-chat-input--txt-wrap._im_text_wrap {
    border-color: #808080;
}
/*Цвет при наведении на диалог, подсветка непрочитанных в диалогах*/

.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic,
.nim-dialog:not(.nim-dialog_deleted):hover {
    box-shadow: inset 2px 0px 0px 0px rgba(98, 133, 175, 1), inset -2px 0px 0px 0px rgba(98, 133, 175, 1);
}
.nim-dialog.nim-dialog_classic:not(.nim-dialog_deleted):hover.nim-dialog_unread-out .nim-dialog--inner-text,
.nim-dialog.nim-dialog_classic.nim-dialog_unread-out .nim-dialog--inner-text {
    background: #d9d9d9;
}
/*фон*/

body {
    background: #fff;
}
/*убрать рамку в диалогах*/

._im_dialogs_search,
._im_dialog_actions,
._im_chat_input_w {
    border-color: #fff!important
}
#stories_feed_wrap {
    display: none
}
/*[[Fontstyle]]*/
/*[[Hidestories]]*/
}
