style.css:
@-moz-document domain("steamcommunity.com"), domain("store.steampowered.com"), domain("help.steampowered.com") {
/*опыт*/

.profile_xp_block_right {
    display: none
}
/*подсказки*/

.community_tooltip,
.store_tooltip,
.jsTooltip {
    background: #e6e6e6!important;
    font: 14px arial!important;
    color: #000!important;
    position: absolute!important;
    border-radius: 3px!important;
box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.75);
}
.ds_options_tooltip > .option {
    font: 14px arial!important;
}
/*шапка*/

html body div#global_header {
    border: none;
    left: 0;
    top: 0;
    box-sizing: border-box;
}
html body div#global_header .content {
    height: 50px;
    right: auto;
}
html body div#global_header div.logo {
    padding: 0;
    margin: 0;
}
html body div#global_header div.logo a {
    display: inline-block;
    margin: 3px 0 0 0;
}
html body div#global_header .menuitem {
    padding: 0 15px;
    font-size: 14px;
    line-height: 50px;
    text-transform: lowercase;
}

html body div#global_header .menuitem:first-letter {
    text-transform: uppercase
}
html body div#global_header .menuitem:hover {
    background: #29465c;
}
html body div#global_header .submenuitem {
    padding: 10px 15px;
    font-size: 14px;
    text-transform: lowercase;
}
html body div#global_header .submenuitem:first-letter {
    text-transform: uppercase
}
html body div#global_header .supernav_container {
    left: 176px;
    display: inline-block;
}
html body div#global_header .submenuitem:hover {
    background: #29465c;
}
html body div#global_header .supernav_content {
    padding: 0;
    box-shadow: none;
    max-width: none;
    width: 175px;
    margin: 4px 0 0 -1px;
}
.header_installsteam_btn,
a.submenuitem:first-child {
    display: none;
}
/*предметы и инвентарь*/

.inventory_iteminfo .item_desc_icon_center {
    width: 348px;
}

/*[[newyearstyle]]*/
/*[[Hidecurators]]*/
/*[[Hidefooter]]*/
}

options.value:
.persona.online,
a.persona.online,
.persona.online a,
.friend_status_online,
.friend_status_online a,
.friend_status_online div,
.profile_header_content .actual_persona_name {
    color: #e4ca63;
}
.playerAvatar.online,
.appHubIconHolder.online,
.playerAvatar.online img,
.avatar_block_status_online,
.appHubIconHolder.online img,
.avatar_block_status_online img,
.friend_status_online .friend_block_avatar img,
.friend_activity.friend_status_online .friend_block_avatar,
.friend_block_holder.friend_status_online .friend_block_avatar {
    background: linear-gradient(145deg, #d5bf6a 10%, #ffe2a9 40%, #ba995c 52%, #ba995c 61%, #d5bf6a 85%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
}
.profile_header_bg {
    background: url("http://i.imgur.com/ea4G8tx.png") no-repeat scroll center top rgba(0, 0, 0, 0);
    max-width: 1057px;
    height: 280px;
    z-index: 2;
}
.profile_header_bg_texture {
    background: none;
}
.profile_header_bg_texture:before {
    background: url("http://i.imgur.com/epqTEVJ.png") no-repeat scroll center top transparent;
    z-index: 1 !important;
    pointer-events: none;
    padding-right: 25px;
    padding-left: 25px;
    position: absolute;
    content: "";
    bottom: 0px;
    right: 0px;
    left: 0px;
    top: 0px;
}
.profile_content {
    overflow: visible !important;
    padding-top: 70px !important;
    margin-top: -70px !important;
    position: relative !important;
}
.profile_customization:not(.none_selected) .profile_customization_header {
    text-shadow: 1px 1px 2px #000000;
    margin-bottom: 40px;
    position: relative;
    color: #e4ca63;
}
.profile_customization:not(.none_selected):before {
    background: url("http://i.imgur.com/tNymtWB.png") no-repeat scroll center top rgba(0, 0, 0, 0) !important;
    position: absolute;
    display: block;
    height: 85px;
    width: 664px;
    left: -15px;
    content: "";
    top: -10px;
    z-index: 0;
}
.profile_customization:not(.none_selected):after {
    background: url("http://i.imgur.com/F8nPY12.png") no-repeat scroll center bottom rgba(0, 0, 0, 0) !important;
    position: absolute;
    display: block;
    width: 664px;
    height: 85px;
    left: -15px;
    content: "";
}
.profile_customization:not(.none_selected) {
    background: url("http://i.imgur.com/AaLta7Q.png") repeat-y scroll center -6px rgba(0, 0, 0, 0) !important;
    margin-bottom: 90px;
    overflow: visible;
}
.profile_customization_editlink_text {
    position: relative;
    color: #000;
}
