/* ==UserStyle==
@name           Inoreader minimal
@namespace      USO Archive
@author         Frikazoid86
@description    `Minimal no ads interface for inoreader`
@version        20200916.12.17
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("inoreader.com") {
/** 2019-03-21 */
/* AD-BLOCK */
#sinner_container,
.leaderboard_ad, 
.block_article_ad, 
.ar_ad, 
.ad_title {
    left: 100% !important;
    position: absolute;
}


#all_read_message,
.inno_dialog,
.inno_dialog_modal_overlay
{
	left: 10% !important;
	position: absolute;
	box-shadow: 0 5px 1em -0.8em #a2d1ee;
	border: 1px solid #567080;
	right: 0!important;
}

.inno_dialog_modal.ui-draggable,
#preferences_dialog_wrapper {
}

.article_tile,
div.article_tile.ar_fixed_width {
	width: 15% !important;
	min-width: 15%!important;
	height: auto;
	padding-bottom: 6%;
	margin: 0.2%;
}

.article_tile_picture {
	height: 60%;
}

.article_tile .article_title_link {
	line-height: 14px;
}

.article_tile_title {
	font-size: 12.5px;
	padding: 4px;
}

.parent_div .parent_div .parent_div_inner {
	padding-left: 20px !important;
}

.articles_feed_group_heading_title,
.articles_feed_group_heading > .articles_feed_group_heading_title {
	padding: 15px 0 0;
	margin: 15px 8em 0;
	border-top: 1px solid #333;
}

.article_tile_footer,
.article_tile_footer_right_buttons{
	padding:0;
	bottom:3px;
}

.articles_feed_group_heading_title {
	padding: 7px !important;
	font-size: 22px;
	margin: 2em 0 0 0 !important;
	border-top: 3px solid #333;
}

.article_tile_title {
	font-size: 13px;
	margin: 0% auto;
}

#reader_pane.reader_pane_sinner {
	padding: 0 !important;
}

.parent_div .parent_div_inner {
	padding: 0 !important;
	margin: 0 !important;
}
.showed {
	padding: 0px;
	margin: 0 0 0;
}

.parent_div.ld1 {
	border: 2px solid transparent;
	border-left-color: #2a3642;
	transition: all 0.4s ease;
}

.parent_div.ld1:hover {
	border-color: #2a3642 transparent #2a3642 #446a91;
}


#reader_pane {
	left: 3em;
}
#tree_pane {
	width: 5em !important;
	padding: 0;
	transition: width 0.5s ease-out;
	border-right: 3px solid #3b5d7d;
}
#tree_pane:hover {
	width: 16% !important;
	box-shadow: 1em 0 4em -2em #0868b7;
}


.parent_div .parent_div .parent_div_inner,
.parent_div .parent_div_inner_ident {
	padding-left: 0px !important;
}

.linkdiv.ld0 {
	border: 1px solid #3b5d7d;
	border-width: 2px 0 0 0;
	background: #2a353e;
}

[class^="icon-"], [class*=" icon-"] {
    color: #90d3ef;
    text-shadow: 0 0 4px;
}
[class^="icon-"]:hover, [class*=" icon-"]:hover {
    color: #bbebff;
    text-shadow: 0 0 6px;
}

.plus_img {
	font-size: 1.8em;
	padding: 1;
	color: #92ccf2;
}

.parent_div_inner,
.parent_div {
	width: 100% !important;
}
.parent_div_link {
	position: relative;
}

.tree_node {
	max-width: 0!important;
	transition: max-width 0.3s ease;
}

#tree_pane:hover .tree_node {
	max-width: 10em!important;
}

#tree_pane:hover .linkdiv,
#tree_pane:hover .tree_link {
	width: 100%;
}
#tree_pane:hover .ld0 {
	width: 93%;
}

.linkdiv,
.tree_link {
	width: 5em;
}

.unread_cnt_wrapper {
	right: 1.5em;
	position: absolute;
}

.article, .article_unreaded {
    width: 49%;
    display: inline-block;
}

}
