style.css:
@-moz-document domain("bs.to") {
html {
    background: radial-gradient(ellipse farthest-corner at center center ,#a73333 0,#000000 100%) fixed rgba(0,0,0,0);
    background: -moz-radial-gradient(ellipse farthest-corner at center center ,#a73333 0,#000000 100%) fixed rgba(0,0,0,0);
    background: -webkit-radial-gradient(ellipse farthest-corner at center center ,#a73333 0,#000000 100%) fixed rgba(0,0,0,0);
}

.andere-serien div.genre, .home>#teaser, .home>.column>section, body.exceeded>section {
    background-image: linear-gradient(#7c2525 0,#870000 24px);
    background-image: -moz-linear-gradient(#7c2525 0,#870000 24px);
    background-image: -webkit-linear-gradient(#7c2525 0,#870000 24px);
}

nav>ul {
    background: linear-gradient(#a73333 0,#670d0d 24px);
    background: -moz-linear-gradient(#a73333 0,#670d0d 24px);
    background: -webkit-linear-gradient(#a73333 0,#670d0d 24px);
    box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110, 17, 17, 0.7) inset;
    -moz-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110,17,17,.7) inset;
    -webkit-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110, 17, 17, 0.7) inset;
}
  
nav>ul>li:hover {
    background: linear-gradient(#9E4444 0,#9C2C2C 24px);
    background: -moz-linear-gradient(#9E4444 0,#9C2C2C 24px);
    background: -webkit-linear-gradient(#9E4444 0,#9C2C2C 24px);
    box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110, 17, 17, 0.7) inset;
    -moz-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110, 17, 17, 0.7) inset;
    -webkit-box-shadow: 0 0 2px rgba(0,0,0,.75),0 -1px 0 rgba(110, 17, 17, 0.7) inset;
}

nav>ul>li>ul>li {
    border-color: #000 !important;
    color: #fff !important;
    background: rgba(37, 0, 0, 0.9) !important;
}

nav>ul>li>ul>li:hover {
    background: rgba(15, 0, 0, 0.9) !important;
}

.serie .frame ul li.active {
    background: #910a0a !important;
}

body>#root {
    box-shadow: 0 0 3px #000;
    -moz-box-shadow: 0 0 3px #000;
    -webkit-box-shadow: 0 0 3px #000;
    background: url(https://abload.de/img/akame_ga_kill_header_y9owa.png) no-repeat #2b2b2b;
}

body>#root>header a.banner {
    background: inherit !important;
}

#root>section {
    background-color: #1F1F1F !important;
    color: #DCDCDC;
}

nav>ul>li {
    border-right: 1px solid #6e1111;
    background: linear-gradient(#a73333 0,#670d0d 24px);
    background: -moz-linear-gradient(#a73333 0,#670d0d 24px);
    background: -webkit-linear-gradient(#a73333 0,#670d0d 24px);
}

form#login {
    box-shadow: 0 5px 10px rgba(0,0,0,.2);
    -moz-box-shadow: 0 5px 10px rgba(0,0,0,.2);
    -webkit-box-shadow: 0 5px 10px rgba(0,0,0,.2);
    background: linear-gradient(#a73333 0,#a10c0c 24px);
    background: -moz-linear-gradient(#a73333 0,#a10c0c 24px);
    background: -webkit-linear-gradient(#a73333 0,#670d0d 24px);
}

    section.navigation>div:first-child {
    color: #f8f8f8;
    border: 1px solid #000000;
    border-radius: 3px 4px 0 0;
    -moz-border-radius: 3px 4px 0 0;
    -webkit-border-radius: 3px 4px 0 0;
    background: linear-gradient(#a73333 0,#670d0d 24px);
    background: -moz-linear-gradient(#a73333 0,#670d0d 24px);
    background: -webkit-linear-gradient(#a73333 0,#670d0d 24px);
}
    
.faq dt:target {
    background: #171717!important;
}

.faq dt:hover {
    background: #161616;
}

.faq dt {
    border: 1px solid #000;
    background: linear-gradient(to bottom,#1c1c1c 0,#2c1414 100%) rgba(0,0,0,0);
    background: -moz-linear-gradient(to bottom,#1c1c1c 0,#2c1414 100%) rgba(0,0,0,0);
    background: -webkit-linear-gradient(to bottom,#1c1c1c 0,#2c1414 100%) rgba(0,0,0,0);
}

#root>header, .home>#teaser>header, .home>.column>section>header, footer>ul, nav>ul {
    background-color: rgba(255, 0, 0, 0.10);
}
  
.hoster-tabs>li.active>a:focus, .hoster-tabs>li.active>a:hover, .serie .hoster-tabs>li.active>a {
    background-color: rgba(255, 255, 255, 0.8);
    border-color: #ddd #ddd transparent;
    color: #555;
}

.andere-serien div.genre ul, .home>#teaser>div, .home>.column>section>div, body.exceeded>section>div {
    background-color: #313131;
}

.andere-serien div.genre ul>li>a, section#defect>div>ul>li>a, section#newest_episodes a>div:first-child, ul#seriesList>li>a.prop {
    color: #C9C9C9;
}

a {
    color: #939393;
}

.serie table.edit tr, .serie table.episodes tr {
    border: 1px solid #000;
}

.serie .scrollbar .handle {
    background: #ff3d3d;
}

.serie .epiInfo {
    background-color: rgba(255, 255, 255, 0.8);
    box-shadow: 0 0 1px 1px rgba(0,0,0,.2);
    color: black;
}

.home #shoutbox dl>dt, .home li:nth-child(even) {
    background-color: #3e3e3e;
}

section#news ul li {
    background-color: inherit !important;
}

section#news ul li .header {
    background-color: #481b1b;
}

.home #shoutbox dl>dt, .home li:nth-child(even) {
    background-color: #3e3e3e;
}

section#news>div>article>header {
    background-color: #654b4b;
}

.nice-select, .nice-select .list {
    background-color: rgba(35, 0, 0, 0.9) !important;
}

.nice-select {
    border-color: rgba(25, 0, 0, 0.9) !important;
}

.nice-select .option.focus, .nice-select .option.selected.focus, .nice-select .option:hover {
    background-color: rgba(15, 0, 0, 0.9) !important;
}

.lSSlideOuter .lSPager.lSpg>li.active a, .lSSlideOuter .lSPager.lSpg>li:hover a {
    background-color: #ca4242;
}

.serie .hoster-player {
    background: rgb(0 0 0 / 30%);
    border: 1px solid #7e6a6a;
    border-radius: 4px;
    overflow: hidden;
    cursor: pointer;
    display: block;
    min-height: 400px;
    width: 100%;
}

.hoster-player>h3, .serie .hoster-player>h2 {
    color: #bbbbbb;
    display: block;
    font-size: 34px;
    margin: 60px auto 0;
    position: relative;
    text-align: center;
}

.serie .hoster-player>h2>u {
    color: #df6b6b;
    text-decoration: none;
}

.serie .hoster-player>.play:before {
    font-family: 'Font Awesome 5 Free';
    font-style: normal;
    font-size: 160px;
    content: "\f144";
    color: #af4141;
    transition: color .3s ease-in;
}

.hoster-tabs>li.active>a:focus, .hoster-tabs>li.active>a:hover, .serie .hoster-tabs>li.active>a {
    background-color: rgba(0, 0, 0, 0.8);
    border-color: #d10000 #000 transparent;
    color: #ffafaf;
}

.serie .hoster-tabs>li.active {
    background-color: rgb(65, 65, 65, 0.8);
    border-color: rgb(65 65 65);
    border-image: none;
    border-style: solid;
    border-width: 1px;
}

.hoster-tabs>li.active>a:focus, .hoster-tabs>li.active>a:hover, .serie .hoster-tabs>li.active>a {
    background-color: rgb(65, 65, 65, 0.8);
    border-color: #d10000 #000 transparent;
    color: #ffafaf;
}
}
