style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



/*------Tabs------*/

.tabbrowser-tabs
{
-moz-appearance: none !important;
background: #000000 url("data:image/png;base64,") repeat-x center !important;
}

.tabbrowser-tab[selected="true"]
{
background: #000000 url("data:image/png;base64,") repeat-x center !important;
color: white !important;
}

.tabbrowser-tab[selected="false"], #content tab:not([selected])
{
background: #000000 url("data:image/png;base64,") repeat-x center !important;
color: #a5b3ce !important;
}

.tabbrowser-tab[selected="false"]:hover, #content tab:not([selected]):hover,
[class="close-button tabs-closebutton"] {
color: white !important;
}

.tabbrowser-tab {
border-top: 0px !important;
border-right: 0px !important;
border-left: 0px !important;
border-bottom: 0px !important;
margin-left: 4px!important;
padding: 0 0 1px 0 !important;
margin-bottom:2px!important;
margin-top:2px!important;
-moz-border-radius: 0px !important;
}

.tabs-bottom {
display:none!important;
}

.tabs-container:not([overflow="true"]) {
-moz-padding-start: 0px !important;
}

.tabbrowser-tab .tab-text {
border: none !important;
}

.tabbrowser-tabs tab[faviconized="true"] {
-moz-border-radius: 0px !important;
padding: 0 !important;
min-width: 32px !important;
max-width: 32px !important;
}


/*------All-Tabs-Button------*/


.tabs-alltabs-button {
-moz-border-top-colors: transparent !important;
}

.tabs-alltabs-button {
-moz-border-radius: 0px !important;
-moz-appearance: none !important;
border-left: 0px !important;
opacity: 0.4 !important;
}
