style.css:
/*
 * facebook RTL 
 *
 * Author      : Amin Diary
 * Updated     : January 19th, 2009
 * Description : RTL wall,post,comment ...
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

.story_body, .caption, .border .story_body, .no_border .story_body, .share_media .owner_comment, .share_media, .story_comment_quote, .story_info, div.story_text, .walltext, .excerpt, .story_content_excerpt, .note_content.clearfix div, .story_content, .comment_cell, .wallcommentbox  {
direction: rtl !important;
text-align: right !important;
}


.share_media .extapp .summary, .caption{
direction: ltr !important;
text-align: left !important;
}

.minifeedwall .expanded_comments .comment_box {
margin-right: 30px;
}
}
