style.css:
@-moz-document domain("www.gaiaonline.com") {
#announcement, #bd-sub, #runway_menu, #yui-gen17, header_content, #c7, #c26, #related_items, #offer_banner, #gcash_menu, #tiespend_header, #sitecomponent_1_38, #sitecomponent_1_34, #dailytreat, #offer_banner, #gcash_menu, #sitecomponent_1_40, #gaia_deals, #gaia_deals_newdeal, #offer_banner, #whats_hot, #gcash_menu, #sitecomponent_1_92, #grid_ad_2, #mygaia-index-ad, #grid_ad_3, #sitecomponent_1_108, #show_on_header_span, #catch_menu, #gaia-hack-modal, #bulletin
 
{display: none;}

/*----- Toned-Down Promote My Post -----*/
    .forum-list .sparkle {
        border-top: none;
        border-bottom: none;
        background: #DBEAF5 !important;
    }
    .forum-list .row_sparkle, .row_sparkle_pg {
        border-top: none;
        border-bottom: none;
        background: none !important;
    }
    .forum-list .gold {
        border-top: none;
        border-bottom: none;
        background: #FAEBA7 !important;
    }
    .forum-list .row_gold, .row_gold_pg {
        border-top: none;
        border-bottom: none;
        background: none !important;
    }
    .forum-list .alert {
        border-top: none;
        border-bottom: none;
        background: #FFD8D6 !important;
    }
    .forum-list .row_alert, .row_alert_pg {
        border-top: none;
        border-bottom: none;
        background: none !important;
    }
    .topic-icon.icon_alert {
        margin-left: 10px !important;
        margin-right: 10px !important;
    }
        /* Luck Chest */
    #treasureChest {display: none!important}
     
    /*Optional, uncomment to apply: Annoying Xmas header gif*/
    /*
     #xmas2k14_event_icon {display:none !important;}
    */
    
    /*------ De-cashify Gaia -----*/
    /* The obnoxious banners below the header on every landing page */ #offer_banner {display: none !important;}

    /* Shop listings for La Victoire, Roccoco, and Club Verge */ div.store_listing:nth-child(2), div.store_listing:nth-child(4), div.store_listing:nth-child(6) {display:none !important;}

    /* Cash item references in Shops landing page tabs */ #shops_landing_rigbox, #shops_landing_premiumitems, #shops_landing_evolving, #shops_landing_collectibles {display:none !important;}

    /* Cash shop listings while inside Shops */ #rococoModernLife, #clubverge, #victoire {display:none !important}

    /* Cash banners */ #gaiacash {display:none !important;}

    /* Daily Treat icon */ #dailytreat {display: none !important;}

    /* Cash amount between gold and achievements*/
       #cta-refill, #cash_amt, #gcash-discount-module {display: none !important;}
       .icon-new.gcash-refill-new, .icon-new.gcash-icon-new {display: none !important;}

    /* Promote My Post button on new thread */ .pimp-my-post {display:none !important;}

    /* Gifting in Forums. It used to point to the gifting page, but only goes to LV now.*/ #buyGift {display:none !important;}

    /* Cash navigation bar and redundant GoFusion link  --- strange and kinda ugly workaround because the GCash tab forces display...sneaky devs.*/ 
    #gcash_menu_content, #gofusion_menu, #gcash_menu {display:none !important;}
    li#gcash_menu {visibility:hidden !important; width: 0px !important;}
    
    /* Cosmetic: Space MyGaia tab to its original position */ #mygaia_menu {margin-left: 60px !important;}

    
    /* Cash in Shops Tab. Removes the big three, plus Premium items and Flynn's Plunder links. */ .panel-link.panel_shops_cash, .panel-link.panel_shops_rococo, .panel-link.panel_shops_mc, .panel-link.panel_shops_flynn {display:none !important}
    
    /* Header crap [CI Gif] */ .override-icon {display:none !important}

    /* Tiered Spending status bar (strange typo in the element name, but I'll account for it anyway) */ #tiespend, #tierspend, .tierspend-event {display:none !important;}

    /* Cosmetic fix for gold display 9/23/14 */
   a[title*="Your Gaia Cash amount. Learn how to get more"] {
    display: none !important;
}
    
    /*----- De-crapify Gaia ------*/
    /* Facebook Share */ .gaia-fb-share-button {display:none !important;}

    /* My Gaia Suggested Content */ .mg_content.suggested, #my_choc, #feed_goldguy {display:none !important;}

    /* Red Unread Announcement/Notice Circles */ #blog-new, #community-new {display:none !important;}

    /* Crunchyroll ad upon loading Flash spaces (Still must wait 5-10 seconds) */ #crunchyroll-preroll {display:none !important;}
}
