style.css:
@-moz-document domain("kinozal.tv"), domain("kinozal.me") {
* {outline:0;} 
.rb_new {display: none;}
.logo_new, .logo  {
	background: #fff url(https://pbs.twimg.com/profile_images/517698907120209920/birornhb_400x400.png) no-repeat 0 22px;
    background-size: 75px;
    min-width: 75px !important;
    padding: 0 0 0 0 !important;
    position: fixed;
    z-index: 1001;  
    top: -23px;
}
.logo_new img, .logo img {
    opacity: 0;
    width: 75px !important;
    height: 90px !important;
}
#header {height: 75px;background: #ffffff;
}
#header .menu {
    width: 100%;
    height: 75px;
    position: fixed;
    top: 0;
    box-shadow: -11px 0px 4px 3px rgba(0,0,0,0.35);
    background: #fff;
    z-index: 1000;
    
}
#header .menu ul {
    position: absolute;
    top: 0;
    width: 620px;
    right: 50%;
    margin-right: -320px; 
}
#header .menu ul li a {
    height: 18px!important;
    color: #616161;
}
#header .menu ul li a:hover {
    background: #fff;
}
#header .menu form {
    background: rgba(255,255,255,0);
    width: 400px;
    height: 24px;
    border: 1px rgba(0,0,0,0.11) solid;
    border-radius: 3px;
    position: absolute;
    top: 23px;
    right: 50%;
    margin-right: -200px;
    padding: 8px 5px;
}
#header .menu form input.inp {
    font-weight: 900;
    width: 360px;
    padding-left: 5px;
    color: #333;
    height: 22px;
    border: 0; /* границы */
    background: #ffffff !important;
}
#header .menu form .s_submit {
    background: url(http://slimshop.ru/images/search_bttn.png) no-repeat scroll left top transparent;
    height: 35px;
    width: 35px;
    margin-top: -5px;
    cursor: pointer;
}
input {outline: none;}
input:-webkit-autofill {
    -webkit-box-shadow: inset 0 0 0 50px #fff !important;
    -webkit-text-fill-color: #999 !important;
    color: #999 !important;
}
#header .zan_l, #header .zan_r {display: none}
}
