style.css:
@-moz-document domain("wowprogramming.com") {
/* 
Bare minimum implementation of a 
dark theme for wowprogramming.com documentation pages.

mythricia at thatGoogleService dot com
*/


/* Color of the surrounding background body */
body
{ background: #1a1a1a !important;
}

/* Colors of the inner content, where all the actual content is*/
#wrapper
{ color: #add;
	background: #242d35;
}

/* Table header used in API listing and Event listing */
th
{ color: #00c2ff;
	background: #2b4154;
}

/* The line / border separating the tables */
td
{ border-color: #8a8a8a;
}

/* Headers, used on misc pages */
h3,
h2
{ color: #00c2ff;
}

/*  Link coloring */
a:link
{ color: #ffc107;
	text-decoration: none;
}
a:visited
{ color: #9e9e9e;
	text-decoration: none;
}
a:hover
{ color: #fff;
	text-decoration: underline;
}

/* Code color for inline code, not actual code block */
code
{ color: #F82;
}

/* Syntax highlingting */
.syntaxhighlighter
{ background-color: #242d35 !important;
}
.syntaxhighlighter .line.alt1
{ background-color: #aaa !important;
}
.syntaxhighlighter .line.alt2
{ background-color: #aaa !important;
}
.syntaxhighlighter .line.highlighted.alt1,
.syntaxhighlighter .line.highlighted.alt2
{ background-color: #e0e0e0 !important;
}
.syntaxhighlighter .line.highlighted.number
{ color: black !important;
}
.syntaxhighlighter .gutter .line
{ color: black !important;
}
.syntaxhighlighter table caption
{ color: black !important;
}
}
