style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("etherpad.mit.edu") {
  #editorcontainer {
	 width:70%;
	 top:59px;
	 left:50px;
	 right:0;
	 bottom:0;
	 z-index:1;
	 overflow:auto;
   border-radius: 10px;
	 -webkit-overflow-scrolling:touch
  }
	#users,.toolbar {
	 background:#f7f7f7;
  }
	.toolbar {
	 background:linear-gradient(#ddd,#ccc 80%);
	 border-bottom:600px solid #ccc;
	 overflow:hidden;
	 padding-top:20px;
	 width:100%;
	 white-space:nowrap;
	 height:10px
  }
	#headerbarlogo {
   height:0px;
  }
  #headerbar {
	 background:#0e72d8;
   border-bottom-left-radius: 5px;
   border-bottom-right-radius: 5px;
	 height:10px
  }

}
