style.css:
@-moz-document domain(wired.com) {

.no-scroll {
    overflow: scroll !important;
}
#global-header {
    box-shadow: none;
    position: absolute;
    -webkit-transition: none;
    transition: none;
}
.pusher,
.brad-top {
    -webkit-transition: none;
    transition: none;
}
.nav-section {
    border: none !important;
}
#post-header.standard {
    padding: 0 !important;
}
.content {
    margin-right: 100px !important;
}
.rad.brad-top.bg-gray-1.flex-box.align-m.justify-c.headroom.hr-not-top.hr-pinned,
#cm-footer,
#cm-footer-cm-unit,
#we-recommend-hdr,
#around-the-web,
#related,
#sidebar,
#sharing,
.advertisement,
.rad.brad-top.bg-gray-1.flex-box.align-m.justify-c.headroom.hr-top,
aside.float-r-big.float-r-med.col.med-col-8.big-col-6.mob-order-2,
.ui-logo {
    display: none;
}
.post {
    left: 0 !important;
}
img {
    height: auto;
    margin-left: 0 !important;
    margin-right: auto;
}
.relative {
    position: static !important;
}
.hr-top #logo-bar {
    background: #FFF !important;
}
h1.hide-mob,
button.meta.subscribe-cta.no-outline {
    color: #000 !important;
}
.ui-logo {
    background-position: -2694px -1px !important;
}
.ui-boxes {
    background-position: -706px 0 !important;
}
.ui-search {
    background-position: -975px -1px !important;
}

}
