style.css:
@-moz-document domain("shinden.pl") {
body{background:black;color:white!important;}
a{color:white!important;}
a:hover{color:aqua!important;}
.logo-char,.logo-title,#cookie-bar,#arrowchat_base{display:none!important;}

.nav.nav-user.nav-user-logged-out{background:black;}
.nav-main{margin-top: 0px!important;background:black;}

.logo {
    position: relative;
    height: 50px;
    width:100%;
    background: black;
}

.l-main-contantainer{width:100%!important;}

.footer>*{background: black!important;}
.player-container{display:block!important}
}

@-moz-document url-prefix("http://forum.shinden.pl"), url-prefix("http://www.forum.shinden.pl") {
[src^='styles/brivium/manganese/xenforo/logo.png']{display:none!important;}
.navTabs{position:absolute;top:0px;margin: 0 auto;}
#navigation .pageContent{margin:0!important;}
#headerMover #headerProxy,#header{height:100px!important;}
}
