@-moz-document url-prefix("https://userstyles.org/styles/") {
/* VER : UTC+8 : 2019.02.10 | 00:07 *//* i really want this to be global */
/* UserStylesBeautify */
/* 顶部尺寸/header top size */
div#root>div>div#header{height: 18pt !important;z-index: 9999;}
#account>a>span{padding: 10px;margin-left: inherit;}
#top_logo>a>img{width: 70px;}
#create_new_style{min-height: 0px;}
div#root>div>#header>div#search_input{height: auto;}
div#header>div#account>a>span,div#header>div#account>a{white-space: pre;}
/* 预览图设置/preview set */
/* 位置/position */div#view>div#style_info>div#preview_image_div{margin-left: 3%;max-width: 350px;max-height: 196px;}
/* 左侧导航设置/left side navigation set */
div>div#root>div.navigation{padding: 0px;width: auto;}
div#nav-view>div#navigation>div:nth-child(3),div#nav-view>div#navigation>div:nth-child(2){display: none;}
div#container>div#nav-view>div#navigation{padding: 0;margin-top: 16%;width: 12px;}
div#container>div#nav-view>div#navigation{position: absolute;left: 0px;}
#view{background-color: #f5f7fa;z-index: 1001;}
/* 左侧导航宽度/left side navigation width */div#nav-view>div#navigation>div:nth-child(n){width: 58%;overflow: hidden;}
/* 字体/font */div#container>div#nav-view>div#navigation *{font-size: 90%;font-family: "Segoe UI","Arial","Microsoft YaHei" !important;}
/* 样式介绍设置/css Description set */
/* 位置/position */div#view>div#style_info>div.text_style_page{position: absolute;left: 52%;/*[[DescriptionPosition]]*/}
/* 360 */OffThisCssCodediv#nav-view>div#view>div#style_info>div.text_style_page{left: 52%;top: 2%;min-width: 47vw !important;}
/* 字体/font */div#view>div#style_info>div.text_style_page,div#view>div#style_info>div.text_style_page>*,div#view>div#style_info>div.text_style_page *{font-size: 93%;font-family: "Consolas_VrF","Microsoft YaHei_VrF","Consolas","Microsoft YaHei"}
#style_info>div.text_style_page{white-space: pre-wrap;}
#style_info>div.text_style_page{max-height: 35vh !important;overflow: scroll !important;}
/* 应用对象连接 */
#applies_link{display: none;}
/* 设置选项设置/style settings set */
/* 设置选项位置/style settings position */div#view>div#style_info>#style-settings{position: relative;left: 55%;margin-top: 1%;max-width: 50vw;}
/* 360 */OffThisCssCodediv#nav-view>div#view>div#style_info>#style-settings{position: relative;left: 55%;margin-top: -103% !important;max-width: 50vw;}
div#view>div#style_info>div#style-settings *{font-family: "Consolas_VrF","Microsoft YaHei_VrF","Consolas","Microsoft YaHei";}
/* 标题设置/title set */
/* 标题位置/title position */div#view>div#style_info>h1#stylish-description{margin-left: 3%;position: relative;margin-top: -6.5%;margin-bottom: 1.5%;}
/* 标题字体/title font */div#view>div#style_info>h1#stylish-description{font-family: "Times New Roman_VrF","Microsoft YaHei_VrF","Times New Roman","Microsoft YaHei";}
/* 作者名字的位置 */
div#view>div#style_info>div#style_author{margin-left: 3%;}
/* 安装信息设置/information set */
div>div#style_info>#information_div{margin-left: 3%;position: relative;margin-top: 3%;margin-bottom: 28%;}
/* 360 */OffThisCssCodediv#nav-view>div#view>div#style_info>div#information_div *{color:rgba(0,0,0,0) !important;text-shadow: transparent 0px 0px 0px !important;}
div#style_info>div#information_div>div#center_information{position: absolute;top: 119%;}
div#style_info>div#information_div>div#right_information{margin-left: 22%;min-width: 50%;position: absolute;top: 119%;}
#left_information>div:nth-child(1)>div:nth-child(n){display: none;}
/* 证书宽度设置/License width */#left_information>div:nth-child(2)>div:nth-child(2){width: 26%;overflow: hidden;white-space: pre;}
/* 安装升级样式按钮设置 */
#ownedButtons>a:nth-child(1)>div{position: fixed;left: 3px;top: 31.5%;z-index: 999;}
#ownedButtons>a:nth-child(2)>div{position: fixed;left: 3px;top: 39%;z-index: 999;}
#style_installed_button,#install_style_button,#update_style_button{margin-left: inherit;position: fixed;left: 3px;top: 12%;z-index: 999;}
/* 隐藏按钮文字 */div#buttons>div#install_style_button>div:nth-child(2),div#buttons>div#update_style_button>div:nth-child(2),div#buttons>div#style_installed_button>div:nth-child(2){display: none;}
/* 安装样式按钮 */div#buttons>div#install_style_button{height: 18px;}
/* 安装样式按钮 */div#buttons>div#install_style_button>div>#install_style_icon{height: 18px;margin-left: -3px;margin-right: 2px;}
/* 安装样式按钮 */div#buttons>div#install_style_button>div>#install_style_icon{display: table-cell;}
/* 安装样式按钮 */div#buttons>#install_style_button:after{content: "Install";}
/* 升级样式按钮 */div#buttons>div#update_style_button{height: 18px;}
/* 升级样式按钮 */div#buttons>div#update_style_button>div>.update-icon{height: 18px;margin-left: -3px;margin-right: 2px;}
/* 升级样式按钮 */div#buttons>div#update_style_button>div>.update-icon{display: table-cell;}
/* 升级样式按钮 */div#buttons>#update_style_button:after{content: "Update";}
/* 安装完成按钮 */div#buttons>div#style_installed_button{height: 18px;}
/* 安装完成按钮 */div#buttons>div#style_installed_button>div>.installed-icon{height: 18px;margin-left: -3px;margin-right: 2px;}
/* 安装完成按钮 */div#buttons>div#style_installed_button>div>.installed-icon{display: table-cell;}
/* 安装完成按钮 */div#buttons>#style_installed_button:after{content: "Finshed";}
#install_style_button,#style_installed_button,#update_style_button,#ownedButtons>a:nth-child(n)>div{width: auto;height: auto;padding: 3px;}
/* 用户脚本设置/userscript set */
div#view>div#style_info>#as_userscript>a{background-size:contain;background-image: url("https://addons.cdn.mozilla.net/user-media/addon_icons/0/748-64.png");}
div#view>div#style_info>#as_userscript>a{color: rgba(0,0,0,0) !important;text-shadow: transparent 0px 0px 0px !important;}
div#view>div#style_info>div#as_userscript{position: fixed;left: 3px;top: 12.5%;z-index: 999;}
div#view>div#style_info>#as_userscript{overflow: hidden ;white-space: pre;width: 6ch;padding: 3px;border-radius: 4px;background-color: yellow;font-weight: bold;}
/* 显示代码设置/css code set */
#button_middle{position: fixed;left: 3px;top: 19%;}
#button_middle>div.css_button{width: auto;padding: 3px;height: 18px;}
#css_text_area_div{position: fixed;z-index: 999;left: 15%;}
div#style_info>div#css_text_area_div>#stylish-code{position: fixed;min-height: 70vh;max-width: 80vw;z-index:999;}
/* 显示代码字体/css code font */div#style_info>div#css_text_area_div>#stylish-code{height: 12px;font-size: 2.1vh;font-family: "Consolas","Microsoft YaHei";}
#button_middle>div.css_button>div.no-select,#button_middle>div.css_button>div.css_close,#button_middle>div.css_button>div.css_open{display: none;}
#button_middle>div.css_button:after{content: "Css Code";}
/* 隐藏高级设置按钮 */#button_middle > div.advanced_button > div.no-select,#button_middle > div.advanced_button > div.advancedsettings_open:after,div#actions_div>div#buttons>div.customize_button{display: none;}
#button_middle > div.advanced_button{width: auto;padding: 3px;height: 18px;margin-left: 0px;}
#button_middle > div.advanced_button{position: fixed;left: 3px;top: 0%;}
#button_middle > div.advanced_button:after{content: "Show Update";}
.advancedsettings_shown{background-color: inherit;padding: 0px;}
/* 分享按钮设置/share button set */
/* 分享按钮位置/share button position */#actions_div{margin-left: 3%;}
/* 捐款设置/Donation set */
#paypal_form{position: absolute;left: 0px;margin-top: 43%;}
#donation_button > div:nth-child(2){display: none;}
#donation_button:after{content: "Donation";}
#donation_button{width: auto;height: auto;padding: 3px;}
/* 反馈设置 */
#style_info > a{position: absolute;left: 0px;margin-top: 55%;}
#send_feedback_button>div:nth-child(2){display: none;}
#send_feedback_button:after{content: "Feedback";}
#send_feedback_button{width: auto;height: auto;padding: 3px;}
/* 注意事项设置/notes set*/
div#view>div#style_info>div#notes_textarea{margin-left: 2%;position: relative;margin-top: 40%;}
/* 帮助论坛的位置 */
div#view>div#style_info>#discussions-area{margin-left: 2%;position: relative;margin-top: 3%;}
/* 页脚的位置 */
div#root>div>div#footer{position: relative;margin-top: 28%;padding-top: 0px;padding-bottom: 10px;}
/* 隐藏无用的删除按钮 */
div#view>div#style_info>a[href*="delete"]{display: none;}
/* 隐藏无用的信息 */
#headline{display: none;}
#navigation>div:nth-child(n)>a:nth-child(n)>div>img{display: none;}
/* 列表控制 */
#advancedsettings_area>div:nth-child(n+1)>div.title_setting,#advancedsettings_area>div:nth-child(n+1)>div:nth-child(n+1),#advancedsettings_area{display: inline-block;}
/* 第一列表与第二列表间隔 */#advancedsettings_area>div:nth-child(n)>div.title_setting{padding-right: 5px;}
/* 颜色拾取器排除颜色选择 */div#style-settings>div#advancedsettings_area>div:nth-child(n)>div:nth-child(n)>div>div:not([class=sketch-picker]){border-radius: 0px !important;max-height: 15px;}
/* 颜色拾取器排除图像选择 */div#style-settings>div#advancedsettings_area>div>div:nth-child(n)>div:not([class=image_options]){padding: 1px !important;display: inline-flex !important;}
/* 颜色拾取器颜色选择背景 */#advancedsettings_area>div>div:nth-child(n)>div:nth-child(n)>div.sketch-picker{height: auto;}
/* 图像选择 url 输入框 */div.setting_div>div>div>.option-user-url{height: auto;}
#advancedsettings_area>div:nth-child(n)>div:nth-child(n)>div:nth-child(n)>div.sketch-picker{margin-left: -60vh;position: fixed;top: 20vh;}
/* 列表行距间隔 */.setting_div{min-height: 0px;}
.text_setting_input{height: auto;border-radius: inherit;margin-bottom: inherit;}
/* 360浏览器控制 */
/* 360 */OffThisCssCodediv#actions_div>div#share_div>div#social_networks:after{display: none !important;}
/* Edge浏览器控制 */
@supports(-ms-ime-align:auto)
{
div#view>div#style_info>div.text_style_page{z-index: 1002;}
div#buttons>div>div#install_button{display: none;}
div#view>div#style_info>div#css_text_area_div{z-index: 1003;}
/* 预览图设置/preview set */
/* 位置/position */div#view>div#style_info>div#preview_image_div{margin-left: 3%;max-width: 370px;min-height: 196px;}
div#view>div#style_info>div#style_author{margin-left: 3%;margin-top: -1%;}
/* 设置选项位置/style settings position */div#view>div#style_info>#style-settings{position: absolute;left: 55%;margin-top: 23%;max-width: 50vw;z-index:1003;}
}
/* FireFox浏览器控制 */
@supports(-moz-animation: none){
/* 标题位置/title position */div#view>div#style_info>h1#stylish-description{margin-left: 3%;position: relative;margin-top: -11vh;float:left;margin-bottom: 1vh;}
/* 设置选项位置/style settings position */div#view>div#style_info>#style-settings{position: relative;left: 55%;margin-top: -28ch;max-width: 50vw;}
/* 注意事项设置/notes set*/
div#view>div#style_info>div#notes_textarea{margin-left: 2%;position: relative;margin-top: 52vh;}
/* 安装信息设置/information set */
div#view>div#style_info>div#information_div{margin-left: 3%;position: relative;margin-top: 13.5vh;margin-bottom: 1vh;}
/* 帮助论坛的位置 */
div#view>div#style_info>#discussions-area{margin-left: 2%;position: relative;margin-top: 16vh;}
/* 捐款设置/Donation set */
#paypal_form{position: absolute;left: 0px;margin-top: 95vh;}
#style_info>div.text_style_page{max-height: 38vh !important;overflow: scroll !important;}
}
}

