style.css:
@-moz-document domain("tucao.one") {
body {
    background-color: #222222;
    background-image:none;
}
.header_logo {
    transform: scale(0.3);
    position: relative;
    padding: 0px;
    top: -72px;
    left: -335px;
    z-index: 10001;
    height: 90px;
}
/*header_logo*/
.top_nav .play,li.home {
	opacity: 0;
}
.top_nav {
    padding-left: 70px;
}
.new_w {
    background-color: #222 !important;
}
.header_nav .nav_link {
    color: white;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
    

/*Top*/
.top {
    background: #222222;
    border: 1px solid #666;
    position: relative;
}
.top_bg div {
    height: 0px;
}
.top_nav a {
    color: #cccccc;
    
}

.header_nav dl {
    border-radius: 3px;
    background-color: rgba(180,13,49,0.75) !important;
}
.header_nav dl a {
    background-color: transparent;

}
.header_nav dl a:hover {
    background-color: rgba(0,0,0,0.45);
    transition-duration: 0.35s;
    transition-timing-function: ease;
}

.top_nav a:hover,.index_pos9 a:hover,.index_pos9_more li a:hover,.lists b:hover,.member_mini_userinfo dt a:hover,.lists_right b:hover,.hot_lists a:hover,.link_partner a:hover,.link_web li a:hover,.list .title:hover,.hot a:hover,.show_user p a:hover,.title_red .more:hover,.show_share dt >*:hover,.footer_info dl dt a:hover,.footer_count dd dt:hover,.footer .footer_info dt:hover, sj_link:hover,.newcatpos a:hover,li.top_up a:hover,.header_nav .nav_link:hover,.title_red .more a:hover,.new_list li a:hover {
    color: #f37;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
.index_pos9 p {
    color: #f37 !important;
}
.lists img,.list img,.lists_right img {
    border-radius: 5px !important;
    opacity:1;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
/*每个视频的图和文字*/
.lists li:hover img,.lists_right li:hover img {
    opacity:0.6;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
.lists li:hover b,.lists_right li:hover b {
    color: #f37;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}

.list .pic:hover {
    opacity:0.6;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
.list .pic:hover + a {
    color: #f37;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
.list .description {
    color: #999 !important;
}
/*box调节*/
.list .box {
	border-radius: 5px;
   border-color: #777;
}
.list li {
    padding: 3px 6px !important;
}
.list .info em {
    color: #999 !important;
}

.title_red .name, .title_blue .name, .title_green .name, .title_black .name, .cat_nav .name, .show_info .now,.hhbox .hht,show_info dt{
    color: #f37 !important;
}
/*顶部背景*/
.main {
	background: #222222;
}
/*左边顶部推荐视频*/
.left {
    background-color: #222222 !important;
}
[style="display: list-item;"] a p {
    color: #f37 !important;
}
div.left.new_w h2 {
    background: #555555;
}

.title_gray .r_bgm {
    color: white;
    background-color: #555;
}

/*分区栏目底色和字体*/
.lists {
    background-color: #222222;
}


/*右边个人信息栏*/
.title_gray {
    background: #555555 !important;
    margin-top: 0px !important;
}
.xf_tabs li {
    color: white;
    transition-duration: 0.35s;
    transition-timing-function: ease;
}
.xf_tabs li:hover {
    background-color: black !important;
    transition-duration: 0.35s;
    transition-timing-function: ease;
}
.member_mini_userinfo {
    background: #222222;
}
.member_mini_userinfo dt a {
    color: #CCCCCC;
}
/*全站信息栏*/
.index_ol {
    background-color: #555555;
}
.index_ol a{
    color: white;
}
/*广告栏*/
.title_gray .name {
    color: white;
}
.index_zt_pos {
    background: #222222;
}
/*排行榜*/
.gray_show {
    background-color: #222222;
}
.hot_lists a,.lists_right b,.hot a,.list .title,.sj_lists a,.newcatpos a,.lists b,.index_pos9_more li a,.index_pos9 a,.xf_list em,.footer_info dl dt a,.footer_count dd dt,.footer .footer_info dt,li.top_up a,.yjlist em,.new_list li a {
    color: #ccc;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
/*右下广告栏*/
body > div:nth-child(8) > div.right > div:nth-child(3) {
    background: #222222;
}
.title_red, .title_blue, .title_green, .title_black, .cat_nav, .show_info, .lists_order {
    background: #555 !important;
    margin-top: 0px;
    border: 1px solid white;
}



/*每日番剧*/

.xf_list li:hover em,.yjlist li:hover em {
    transition-duration: 0.25s;
    transition-timing-function: ease;
    color: #f37;
}
/*胖次*/
.hhbox .hht {
	background: #555555;   
}
.hhbox .hhl {
    background: #222222;
}
.hhbox .hhl img {
    opacity:1;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}
.hhbox .hhl img:hover {
    opacity:0.5;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}

/*合作伙伴和友情链接*/
.link .name {
    background-color: #555555 !important;
    color: white;
}
.link_partner a,.link_web li a {
    color: #aaaaaa;
    transition-duration: 0.25s;
    transition-timing-function: ease;
}

/*footer*/
.footer_info dl dd a,.footer .footer_info dd{
    color:white;
}
/*页脚备号*/
.footer .footer_by {
    background-color: #555555;
}

/*子页面背景色*/
.list {
    background: #222222;
}
.pagego a {
    background-color: #777;
}
.newpages a, .newpages span, .selectpages select {
    color: #ccc;
    border:1px solid #555;
}
select {
    color: black !important;
}
/*广告条背景色*/
.a2013d {
    margin-top: 0px;
    background-color: #222222;
}
[style="width:300px;background-color:#FFF;padding-left:24px;"] {
    background-color: #222222 !important;
}

/*播放页面*/
.show_title {
	color: #cccccc !important;
}
.show_content {
    background: #222222;
}
.show_content >* {
    color: #cccccc !important;
}
.new_tool p {
    color: #cccccc;
}


/*投稿者信息栏*/
.right {
    background-color: #222222 !important;
}
.title_gray .r_user {
    color: white;
}
.show_user dt {
    color: #CCCCCC;
}
.show_user p a {
    color: #CCCCCC;
}
.show_user img {
    border-radius: 32px;
}
/*评论框*/
.show_key {
    background-color: #222222;
}
.show_key .on b, .show_key a {
    color: white;
}

.title_red .more,.title_red .more a, .title_gray .more, .sj_link {
    color: white;
}

.comment_list .c {
    color: #cccccc;
}
.comment_list .user {
    background-color: #333;
}
.comment_list .c {
    background-color: #222222;
}
.comment_reply td {
	background-color: #333;
}
.comment_reply {
    background-color: #333;
}
.show_info dt {
    color: #cccccc;
}
.show_share {
    background-color: #333 !important;
    color: #cccccc !important;
}
.show_share dt >*{
    color: #cccccc !important;
}
/*悬浮信息框*/

[id^="lists_tip"] {
    background-color: rgba(0,0,0,0.85) !important;
    position:relative;
}
li.tip_desc,dt.tipt_desc {
    color: #ccc !important;
}
li.tip_title,dt.tipt_title {
    color: #f37 !important;
}

/************是否去除广告*************/
.index_zt_pos,.a3_poster,.a1_poster,[style="padding-top:12px;"],.a2_poster,.x1_poster,.c1_poster,.c2_poster,.c4_poster,.a2013d,.b1_poster,.b2_poster,[style="margin-top:0px;width:964px;height:115px;"] {
    display: /*[[show]]*/ !important;
}
}
