/*---------------width------------*/

#header {
	width: 250px;
}
#sections {
	padding-left: 20rem;
}
#stylish-manage #installed {
	margin-left: 300px;
	padding: 20px 0;
}
body {
	background: #262A2E;
	color: #B3B3B3;
}
#header {
	border-right: 1px solid #2d2d2d;
	-webkit-box-shadow: 0 0 2rem 0.1rem black;
	overflow-y: auto;
	height: 94%;
}
#sections h2 {
	margin-left: 1.5rem;
}
/*-------Sidebar A-----------*/

#header h1:before {
	font-family: 'Pacifico';
	font-size: 54px !important;
	color: #1e5d8c;
	line-height: 1;
	text-align: center;
	content: "Stylish";
	display: block;
	margin: 10px 0 20px 0;
	padding: 0;
	border-bottom: 1px solid #444;
}
#header h1:hover:before {
	text-shadow: 0 1px 0px #1e5d8c, 1px 0 0px #1e5d8c, 0 -1px 0px #1e5d8c, -1px 0 0px #1e5d8c !important;
	color: #2d2d2d !important;
	content: "Hi beauty";
}
#header h1 {
	color: #8BC34A;
	text-align: center;
	font-family: "Pacifico";
}
h2,
span#sections-heading {
	color: #8BC34A
}
/*-----PRE---------*/

.cm-s-default .cm-builtin {
	color: #0051a7;
}
/*-blue-*/

.CodeMirror span {
	color: #99b8b8;
}
/*-gray~blue-*/

.cm-s-default .cm-qualifier,
.CodeMirror-matchingbracket {
	color: #16ff6e;
}
/*-green-*/

.cm-s-default .cm-atom {
	color: #eb5368;
}
/*-red~pink-*/

.cm-s-default .cm-keyword {
	color: #b821ce;
}
/*-violet-*/

.cm-s-default .cm-string {
	color: #aa8a11;
}
/*-shitcolor-*/

.CodeMirror div.CodeMirror-cursor {
	border-left: 2px solid cyan;
}
.cm-s-default .cm-comment {
	color: #565656;
}
/*--*/
#menu {
    background-color: #262A2E;
    border: 1px solid #33393e;
    }
.CodeMirror {
	background: #1c2023;
	border: 1px solid #33393e;
	color: #B3B3B3
}
.CodeMirror-gutters {
	border-right: 0px solid red;
	background-color: #33393E;
}
.CodeMirror-activeline-background {
	background: rgba(54, 54, 54, 1);
}
/*-active line-*/

.CodeMirror-selected {
	background: rgba(54, 54, 54, 1) !important;
}
/*-selected text-*/

.CodeMirror-matchingbracket {
	outline: solid 1px #16ff6e;
}
/*----------search result--------*/

.cm-searching {
	background: rgba(252, 214, 5, 0.8);
}
/*---------------------Buttons--------------*/
button {
	cursor: default;
	background: #33393e;
	padding: 10px 15px;
	text-align: center;
	border: 1px solid rgba(255, 255, 255, 0.1) !important;
	color: #a5aeb5;
	font-family: 'Roboto';
	font-size: 12px;
	font-weight: 700;
	text-transform: uppercase;
	overflow: hidden;
	outline: transparent !important;
}
button:hover {
	background-color: #2a333a;
}
#actions button {
	margin: 0 0 3px;
}
#save-button,
#beautify,
#cancel-button {
	width: 32% !important;
	padding: 15px !important;
	font-size: 0 !important;
}
#from-mozilla,
#to-mozilla {
	width: 49%;
}
#save-button:before,
#beautify:before,
#cancel-button:before,
#from-mozilla:before,
#to-mozilla:before,
button.remove-applies-to:before,
button.add-applies-to:before,
button.remove-section:before,
button.add-section:before,
button.beautify-section:before {
	font-family: 'FontAwesome';
	display: inline-block;
	vertical-align: top;
	font-weight: normal;
	margin-right: 7px;
}
#save-button:before,
#beautify:before,
#cancel-button:before {
	font-size: 24px;
	line-height: 24px;
	width: 24px;
	height: 24px;
	text-align: center !important;
