	line-height: 16px;
	width: 16px;
	height: 16px;
}
#save-button:before {
	content: '\f0c7';
	color: #8bc34a;
}
#save-button {
	color: #8bc34a;
}
#save-button:hover {
	background-color: #404b33;
}
#save-button:after {
	content: 'd';
}
body.dirty #save-button,
body.dirty #save-button:before {
	color: #F44336;
}
body.dirty #save-button:hover {
	background-color: #54312f;
}
body.dirty #save-button:after {
	content: '?';
}
#beautify {
	color: #d0a421;
}
#beautify:hover {
	background-color: #4e452a;
}
#beautify:before {
	content: '\f1c9';
	color: #d0a421;
}
#cancel-button {
	color: #a5aeb5;
}
#cancel-button:hover {
	background-color: #2a333a;
}
#cancel-button:before {
	content: '\f00b';
	color: #a5aeb5;
}
#from-mozilla:before {
	content: '\f093';
}
#to-mozilla:before {
	content: '\f019';
}
button.remove-applies-to {
	font-size: 0;
	padding: 10px !important;
	margin-right: 3px;
}
