	background-color: #54312f;
}
button.remove-applies-to:before {
	content: '\f068';
	color: #F44336;
	margin-right: 0 !important;
	text-align: center !important;
}
button.add-applies-to {
	font-size: 0;
	padding: 10px !important;
}
button.add-applies-to:hover {
	background-color: #404b33;
}
