	content: '\f068';
}
button.add-section:before {
	content: '\f067';
}
button.beautify-section:before {
	content: '\f1c9';
}
/*------options----------*/

input[type=checkbox] {
	-webkit-appearance: textfield;
	position: relative;
	background-color: #262a2e;
	border-radius: 50%;
	color: #fff;
	cursor: auto;
	display: block;
	margin: 0 5px 0 20px !important;
	padding: 0;
	outline: transparent !important;
	width: 15px;
	height: 15px;
	float: left;
	border: 2px solid #474c51;
}
input[type=checkbox]:after {
	content: '';
	display: block;
	position: absolute;
	left: 0;
	top: 0;
	width: 15px;
	height: 15px;
	margin-left: -16px;
	margin-top: -16px;
	background: #8BC34A;
	border-radius: 100%;
	opacity: .2;
	transform: scale(0);
}
input[type=checkbox]:checked {
	background-color: #1e5d8c;
	border: 2px solid transparent;
	color: #2d2d2d;
}
input[type=checkbox]:checked:before {
	font-family: 'FontAwesome';
	color: #2d2d2d;
	content: '\f00c';
	font-size: 10px;
	line-height: 1;
	margin: 0;
	text-align: center;
	font-weight: 200;
}
/*--------input-------------*/

input,
select {
	background-color: #181818;
	color: #B3B3B3;
	border-width: 0px;
	padding: 5px;
}
/*-------------------Manage------------------*/

