{   background: linear-gradient(-45deg, rgba(0, 0, 0, 0) 5px, #fff 5px, #fff 6px, rgba(0, 0, 0, 0) 6.5px) no-repeat, linear-gradient(45deg, rgba(0, 0, 0, 0) 5px, #fff 5px, #fff 6px, rgba(0, 0, 0, 0) 6.5px) no-repeat;}
